check-suspended-users.js.content:
import { connectToMongoDB, User } from './server/mongodb.ts';

async function checkSuspendedUsers() {
  try {
    await connectToMongoDB();

    console.log('Checking all users and their status...');
    const users = await User.find({}, 'firstName lastName email standing isActive suspendedAt suspensionReason').exec();

    console.log('\nAll users:');
    users.forEach(user => {
      console.log(`- ${user.firstName} ${user.lastName} (${user.email})`);
      console.log(`  standing: ${user.standing}`);
      console.log(`  isActive: ${user.isActive}`);
      console.log(`  suspendedAt: ${user.suspendedAt}`);
      console.log(`  suspensionReason: ${user.suspensionReason}`);
      console.log('');
    });

    console.log('\nFiltering suspended users...');
    const suspendedUsers = users.filter(u => u.standing === 'suspended');
    console.log(`Found ${suspendedUsers.length} suspended users:`);
    suspendedUsers.forEach(user => {
      console.log(`- ${user.firstName} ${user.lastName} (${user.email}) - standing: ${user.standing}`);
    });

    console.log('\nFiltering inactive users...');
    const inactiveUsers = users.filter(u => !u.isActive);
    console.log(`Found ${inactiveUsers.length} inactive users:`);
    inactiveUsers.forEach(user => {
      console.log(`- ${user.firstName} ${user.lastName} (${user.email}) - isActive: ${user.isActive}`);
    });

    process.exit(0);
  } catch (error) {
    console.error('Error checking suspended users:', error);
    process.exit(1);
  }
}

checkSuspendedUsers();

check-user-suspension.js.content:
import mongoose from 'mongoose';
import dotenv from 'dotenv';
dotenv.config();

// Connect to MongoDB
mongoose.connect(process.env.DATABASE_URL || 'mongodb://localhost:27017/naeberly');

// Define User schema (simplified)
const userSchema = new mongoose.Schema({
  email: String,
  role: String,
  flagsReceived: { type: Number, default: 0 },
  suspension: {
    isActive: { type: Boolean, default: false },
    startDate: Date,
    endDate: Date,
    reason: String
  }
}, { collection: 'users' });

const User = mongoose.model('User', userSchema);

async function checkUserSuspension() {
  try {
    const user = await User.findOne({ email: 'mlp.yashvantgupta@gmail.com' });

    if (!user) {
      console.log('User not found');
      return;
    }

    console.log('User Details:');
    console.log('Email:', user.email);
    console.log('Role:', user.role);
    console.log('Flags Received:', user.flagsReceived || 0);
    console.log('Suspension Status:', user.suspension || 'No suspension data');

    if (user.suspension && user.suspension.isActive) {
      console.log('\nüö® ACCOUNT IS SUSPENDED');
      console.log('Reason:', user.suspension.reason);
      console.log('Start Date:', user.suspension.startDate);
      console.log('End Date:', user.suspension.endDate);

      const now = new Date();
      const daysRemaining = Math.ceil((user.suspension.endDate - now) / (1000 * 60 * 60 * 24));
      console.log('Days Remaining:', daysRemaining);
    } else {
      console.log('\n‚úÖ Account is NOT suspended');
    }

  } catch (error) {
    console.error('Error:', error);
  } finally {
    mongoose.connection.close();
  }
}

checkUserSuspension();

