  // Background job for sending post-call feedback emails
  const sendPostCallFeedbackEmails = async () => {
    try {
      console.log("Running post-call feedback email job...");
      const now = new Date();
      const oneHourAgo = new Date(now.getTime() - 60 * 60 * 1000); // 1 hour ago

      // Get all calls that ended between 30 minutes and 1 hour ago
      const recentlyCompletedCalls = await storage.getAllCalls();
      console.log(
        `Found ${recentlyCompletedCalls.length} total calls in database`,
      );

      // Check for calls that should be auto-completed (scheduled calls that have passed their time)
      // For testing: Use 5 minutes instead of 30 minutes to auto-complete calls quickly
      const fiveMinutesAgo = new Date(now.getTime() - 5 * 60 * 1000);
      const twentyFourHoursAgo = new Date(now.getTime() - 24 * 60 * 60 * 1000);

      // Auto-complete scheduled calls that have passed their scheduled time
      for (const call of recentlyCompletedCalls) {
        console.log(
          `Checking call ${call._id}: status=${call.status}, scheduledAt=${call.scheduledAt}, completedAt=${call.completedAt}`,
        );

        // Fix completed calls that don't have completedAt timestamp
        if (call.status === "completed" && !call.completedAt) {
          console.log(
            `Fixing completed call ${call._id} - adding completedAt timestamp`,
          );
          try {
            const completedAt = new Date();
            await storage.updateCall(call._id.toString(), {
              completedAt: completedAt,
            });
            call.completedAt = completedAt;
            console.log(`Successfully added completedAt to call ${call._id}`);
          } catch (error) {
            console.error(
              `Failed to add completedAt to call ${call._id}:`,
              error,
            );
          }
        }

        if (call.status === "scheduled" && call.scheduledAt) {
          const scheduledTime = new Date(call.scheduledAt);
          const callEndTime = new Date(
            scheduledTime.getTime() + 15 * 60 * 1000,
          ); // Add 15 minutes duration

          console.log(
            `Call ${call._id}: scheduled=${scheduledTime}, endTime=${callEndTime}, fiveMinutesAgo=${fiveMinutesAgo}`,
          );

          // If the call end time was more than 5 minutes ago, auto-complete it
          if (callEndTime <= fiveMinutesAgo) {
            console.log(
              `Auto-completing scheduled call ${call._id} that ended at ${callEndTime}`,
            );
            try {
              const completedAt = new Date();
              await storage.updateCall(call._id.toString(), {
                status: "completed",
                completedAt: completedAt,
              });
              call.status = "completed";
              call.completedAt = completedAt;
              console.log(`Successfully auto-completed call ${call._id}`);
            } catch (error) {
              console.error(`Failed to auto-complete call ${call._id}:`, error);
            }
          }
        }
      }

      // Re-fetch all calls to get the latest completedAt timestamps
      const updatedCalls = await storage.getAllCalls();

      // Now process calls that have been explicitly marked as completed (including auto-completed ones)
      const callsToProcess = updatedCalls.filter((call) => {
        console.log(
          `Filtering call ${call._id}: status=${call.status}, completedAt=${call.completedAt}`,
        );
        if (call.status !== "completed") {
          console.log(`Call ${call._id} filtered out: status is not completed`);
          return false;
        }
        if (!call.completedAt) {
          console.log(
            `Call ${call._id} filtered out: no completedAt timestamp`,
          );
          return false;
        }
        const completedTime = new Date(call.completedAt);
        const isWithinTimeRange =
          completedTime >= twentyFourHoursAgo && completedTime <= now;
        console.log(
          `Call ${call._id}: completedTime=${completedTime}, twentyFourHoursAgo=${twentyFourHoursAgo}, now=${now}, withinRange=${isWithinTimeRange}`,
        );
        return isWithinTimeRange;
      });

      console.log(
        `Found ${callsToProcess.length} calls that were marked as completed in the last 24 hours`,
      );

      if (callsToProcess.length > 0) {
        console.log(
          "Calls to process:",
          callsToProcess.map((call) => ({
            id: call._id,
            completedAt: call.completedAt,
            status: call.status,
            salesRepId: call.salesRepId,
            decisionMakerId: call.decisionMakerId,
          })),
        );
      }

      for (const call of callsToProcess) {
        try {
          // Check if feedback emails already sent
          const dmFeedbackExists = await storage.getInitialFeedback(
            call._id.toString(),
            "decision_maker",
          );
          const repFeedbackExists = await storage.getInitialFeedback(
            call._id.toString(),
            "sales_rep",
          );

          // Get user details
          const salesRep = await storage.getUser(call.salesRepId);
          const decisionMaker = await storage.getUser(call.decisionMakerId);

          if (!salesRep || !decisionMaker) {
            console.log(`Skipping call ${call._id} - missing user data`);
            continue;
          }

          // Send DM feedback email if not already sent
          if (!dmFeedbackExists) {
            console.log(
              `Sending post-call feedback email to DM: ${decisionMaker.email}`,
            );
            await sendPostCallFeedbackToDM(
              decisionMaker.email,
              decisionMaker.firstName,
              salesRep.firstName,
              call._id.toString(),
            );
          }

          // Send Rep feedback email if not already sent
          if (!repFeedbackExists) {
            console.log(
              `Sending post-call feedback email to Rep: ${salesRep.email}`,
            );
            await sendPostCallFeedbackToRep(
              salesRep.email,
              salesRep.firstName,
              decisionMaker.firstName,
              call._id.toString(),
            );
          }
        } catch (emailError) {
          console.error(
            `Error sending feedback email for call ${call._id}:`,
            emailError,
          );
        }
      }
    } catch (error) {
      console.error("Error in post-call feedback email job:", error);
    }
  };

