import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import TeamManagement from "@/components/TeamManagement";
import CreditsOverview from "@/components/CreditsOverview";
import DMTracking from "@/components/DMTracking";
import CallActivityLog from "@/components/CallActivityLog";
import PerformanceAnalytics from "@/components/PerformanceAnalytics";
import AccountSettings from "@/components/AccountSettings";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import {
  Building2,
  Users,
  Shield,
  TrendingUp,
  Calendar,
  Mail,
  Settings,
  UserPlus,
  CheckCircle,
  XCircle,
  Clock,
  Activity,
  BarChart3,
  Globe,
  Lock,
  Trash2,
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { z } from "zod";

// Enterprise user management schema
const enterpriseUserSchema = z.object({
  email: z.string().email("Invalid email address"),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  role: z.enum(["sales_rep", "decision_maker"], {
    required_error: "Role is required",
  }),
  jobTitle: z.string().optional(),
  department: z.string().optional(),
  password: z
    .string()
    .min(8, "Password must be at least 8 characters")
    .regex(
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]/,
      "Password must contain uppercase, lowercase, number and special character",
    ),
});

export default function EnterpriseAdmin() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isCreateUserOpen, setIsCreateUserOpen] = useState(false);

  // Fetch enterprise analytics
  const { data: analytics, isLoading: analyticsLoading } = useQuery({
    queryKey: ["/api/enterprise-admin/analytics"],
    retry: false,
  });

  // Fetch company users
  const { data: companyUsers = [], isLoading: usersLoading } = useQuery({
    queryKey: ["/api/enterprise-admin/users"],
    retry: false,
  });

  // Fetch domain settings
  const { data: domainSettings, isLoading: domainLoading } = useQuery({
    queryKey: ["/api/enterprise-admin/domain-settings"],
    retry: false,
  });

  // Create user form
  const form = useForm({
    resolver: zodResolver(enterpriseUserSchema),
    defaultValues: {
      email: "",
      firstName: "",
      lastName: "",
      role: "sales_rep",
      jobTitle: "",
      department: "",
      password: "",
    },
  });

  // Create enterprise user mutation
  const createUserMutation = useMutation({
    mutationFn: async (userData) => {
      return await apiRequest("/api/enterprise-admin/create-user", {
        method: "POST",
        body: JSON.stringify(userData),
      });
    },
    onSuccess: () => {
      toast({
        title: "User Created",
        description: "Enterprise user has been created successfully",
      });
      queryClient.invalidateQueries({
        queryKey: ["/api/enterprise-admin/users"],
      });
      queryClient.invalidateQueries({
        queryKey: ["/api/enterprise-admin/analytics"],
      });
      setIsCreateUserOpen(false);
      form.reset();
    },
    onError: (error) => {
      toast({
        title: "Creation Failed",
        description: error.message || "Failed to create user",
        variant: "destructive",
      });
    },
  });

  // Update user status mutation
  const updateUserStatusMutation = useMutation({
    mutationFn: async ({ userId, isActive }) => {
      return await apiRequest(`/api/enterprise-admin/users/${userId}/status`, {
        method: "PATCH",
        body: JSON.stringify({ isActive }),
      });
    },
    onSuccess: () => {
      toast({
        title: "User Updated",
        description: "User status has been updated",
      });
      queryClient.invalidateQueries({
        queryKey: ["/api/enterprise-admin/users"],
      });
    },
    onError: (error) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update user",
        variant: "destructive",
      });
    },
  });

  // Delete user mutation
  const deleteUserMutation = useMutation({
    mutationFn: async (userId) => {
      return await apiRequest(`/api/enterprise-admin/users/${userId}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      toast({
        title: "User Deleted",
        description: "User has been permanently deleted",
      });
      queryClient.invalidateQueries({
        queryKey: ["/api/enterprise-admin/users"],
      });
      queryClient.invalidateQueries({
        queryKey: ["/api/enterprise-admin/analytics"],
      });
    },
    onError: (error) => {
      toast({
        title: "Delete Failed",
        description: error.message || "Failed to delete user",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data) => {
    createUserMutation.mutate(data);
  };

  const handleStatusToggle = (userId, currentStatus) => {
    updateUserStatusMutation.mutate({
      userId,
      isActive: !currentStatus,
    });
  };

  const handleDeleteUser = (userId, userName) => {
    if (
      window.confirm(
        `Are you sure you want to permanently delete ${userName}? This action cannot be undone.`,
      )
    ) {
      deleteUserMutation.mutate(userId);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-slate-50 pt-16">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div className="flex items-center">
              <Building2 className="text-blue-600 mr-3" size={32} />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">
                  Enterprise Admin
                </h1>
                <p className="text-sm text-gray-600">
                  Manage company users and settings
                </p>
              </div>
            </div>
            <Badge className="bg-blue-100 text-blue-800 border border-blue-200">
              <Shield className="mr-1 h-3 w-3" />
              Enterprise Access
            </Badge>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <Tabs defaultValue="overview" className="space-y-6">
          <div className="w-full overflow-x-auto">
            <TabsList className="inline-flex h-10 items-center justify-start rounded-md bg-muted p-1 text-muted-foreground min-w-full lg:min-w-0">
              <TabsTrigger value="overview" className="whitespace-nowrap">
                Overview
              </TabsTrigger>
              <TabsTrigger value="users" className="whitespace-nowrap">
                Users
              </TabsTrigger>
              <TabsTrigger value="team" className="whitespace-nowrap">
                Team
              </TabsTrigger>
              <TabsTrigger value="credits" className="whitespace-nowrap">
                Credits
              </TabsTrigger>
              <TabsTrigger value="dms" className="whitespace-nowrap">
                DM Tracking
              </TabsTrigger>
              <TabsTrigger value="calls" className="whitespace-nowrap">
                Call Activity
              </TabsTrigger>
              <TabsTrigger value="performance" className="whitespace-nowrap">
                Performance
              </TabsTrigger>
              <TabsTrigger value="settings" className="whitespace-nowrap">
                Settings
              </TabsTrigger>

              <TabsTrigger value="analytics" className="whitespace-nowrap">
                Analytics
              </TabsTrigger>
            </TabsList>
          </div>

          {/* Overview Tab */}
          <TabsContent value="overview" className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <Card className="border border-gray-200 shadow-lg bg-white">
                <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                  <CardTitle className="text-sm font-medium text-gray-900">
                    Total Company Users
                  </CardTitle>
                  <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center">
                    <Users className="h-4 w-4 text-blue-600" />
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold text-gray-900">
                    {analytics?.totalUsers || 0}
                  </div>
                  <p className="text-xs text-gray-600">
                    +{analytics?.newUsersThisMonth || 0} from last month
                  </p>
                </CardContent>
              </Card>

              <Card className="border border-gray-200 shadow-lg bg-white">
                <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                  <CardTitle className="text-sm font-medium text-gray-900">
                    Active Sales Reps
                  </CardTitle>
                  <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center">
                    <TrendingUp className="h-4 w-4 text-green-600" />
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold text-gray-900">
                    {analytics?.activeSalesReps || 0}
                  </div>
                  <p className="text-xs text-gray-600">
                    {analytics?.salesRepGrowth || 0}% growth rate
                  </p>
                </CardContent>
              </Card>

              <Card className="border border-gray-200 shadow-lg bg-white">
                <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                  <CardTitle className="text-sm font-medium text-gray-900">
                    Monthly Meetings
                  </CardTitle>
                  <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center">
                    <Calendar className="h-4 w-4 text-purple-600" />
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold text-gray-900">
                    {analytics?.monthlyMeetings || 0}
                  </div>
                  <p className="text-xs text-gray-600">
                    {analytics?.meetingTrend || 0}% vs last month
                  </p>
                </CardContent>
              </Card>
            </div>

            {/* Quick Actions */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Activity className="text-blue-500 mr-3" size={20} />
                  Quick Actions
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <Dialog
                    open={isCreateUserOpen}
                    onOpenChange={setIsCreateUserOpen}
                  >
                    <DialogTrigger asChild>
                      <Button className="h-16 flex flex-col items-center justify-center space-y-2">
                        <UserPlus size={20} />
                        <span>Add Company User</span>
                      </Button>
                    </DialogTrigger>
                    <DialogContent>
                      <DialogHeader>
                        <DialogTitle>Create Enterprise User</DialogTitle>
                        <DialogDescription>
                          Add a new user to your company domain
                        </DialogDescription>
                      </DialogHeader>
                      <Form {...form}>
                        <form
                          onSubmit={form.handleSubmit(onSubmit)}
                          className="space-y-4"
                        >
                          <div className="grid grid-cols-2 gap-4">
                            <FormField
                              control={form.control}
                              name="firstName"
                              render={({ field }) => (
                                <FormItem>
                                  <FormLabel>First Name</FormLabel>
                                  <FormControl>
                                    <Input
                                      {...field}
                                      placeholder="Enter first name"
                                    />
                                  </FormControl>
                                  <FormMessage />
                                </FormItem>
                              )}
                            />
                            <FormField
                              control={form.control}
                              name="lastName"
                              render={({ field }) => (
                                <FormItem>
                                  <FormLabel>Last Name</FormLabel>
                                  <FormControl>
                                    <Input
                                      {...field}
                                      placeholder="Enter last name"
                                    />
                                  </FormControl>
                                  <FormMessage />
                                </FormItem>
                              )}
                            />
                          </div>

                          <FormField
                            control={form.control}
                            name="email"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Company Email</FormLabel>
                                <FormControl>
                                  <Input
                                    {...field}
                                    type="email"
                                    placeholder="Enter your company email"
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={form.control}
                            name="password"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Password</FormLabel>
                                <FormControl>
                                  <Input
                                    {...field}
                                    type="password"
                                    placeholder="Enter secure password"
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={form.control}
                            name="role"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Role</FormLabel>
                                <Select
                                  onValueChange={field.onChange}
                                  defaultValue={field.value}
                                >
                                  <FormControl>
                                    <SelectTrigger>
                                      <SelectValue placeholder="Select role" />
                                    </SelectTrigger>
                                  </FormControl>
                                  <SelectContent>
                                    <SelectItem value="sales_rep">
                                      Sales Representative
                                    </SelectItem>
                                    <SelectItem value="decision_maker">
                                      Decision Maker
                                    </SelectItem>
                                  </SelectContent>
                                </Select>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <div className="grid grid-cols-2 gap-4">
                            <FormField
                              control={form.control}
                              name="jobTitle"
                              render={({ field }) => (
                                <FormItem>
                                  <FormLabel>Job Title (Optional)</FormLabel>
                                  <FormControl>
                                    <Input
                                      {...field}
                                      placeholder="Ex. Sales Manager"
                                    />
                                  </FormControl>
                                  <FormMessage />
                                </FormItem>
                              )}
                            />
                            <FormField
                              control={form.control}
                              name="department"
                              render={({ field }) => (
                                <FormItem>
                                  <FormLabel>Department (Optional)</FormLabel>
                                  <FormControl>
                                    <Input
                                      {...field}
                                      placeholder="Ex. Human Resource"
                                    />
                                  </FormControl>
                                  <FormMessage />
                                </FormItem>
                              )}
                            />
                          </div>

                          <Button
                            type="submit"
                            className="w-full"
                            disabled={createUserMutation.isPending}
                          >
                            {createUserMutation.isPending
                              ? "Creating..."
                              : "Create User"}
                          </Button>
                        </form>
                      </Form>
                    </DialogContent>
                  </Dialog>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Users Tab */}
          <TabsContent value="users">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Users className="text-green-500 mr-3" size={20} />
                  Company Users
                </CardTitle>
                <CardDescription>
                  Manage users from your verified company domain
                </CardDescription>
              </CardHeader>
              <CardContent>
                {usersLoading ? (
                  <div className="text-center py-8">Loading users...</div>
                ) : companyUsers.length > 0 ? (
                  <div className="space-y-4">
                    {companyUsers.map((user) => (
                      <div
                        key={user.id}
                        className="flex items-center justify-between p-4 border rounded-lg"
                      >
                        <div className="flex items-center space-x-4">
                          <div
                            className={`w-10 h-10 rounded-full flex items-center justify-center ${
                              user.role === "sales_rep"
                                ? "bg-blue-100 text-blue-700"
                                : "bg-purple-100 text-purple-700"
                            }`}
                          >
                            {user.firstName?.charAt(0)}
                            {user.lastName?.charAt(0)}
                          </div>
                          <div>
                            <div className="font-medium">
                              {user.firstName} {user.lastName}
                            </div>
                            <div className="text-sm text-gray-600">
                              {user.email}
                            </div>
                            <div className="text-xs text-gray-500">
                              {user.jobTitle} ‚Ä¢ {user.department}
                            </div>
                          </div>
                        </div>
                        <div className="flex items-center space-x-3">
                          <Badge
                            className={
                              user.role === "sales_rep"
                                ? "bg-blue-100 text-blue-800"
                                : "bg-purple-100 text-purple-800"
                            }
                          >
                            {user.role === "sales_rep"
                              ? "Sales Rep"
                              : "Decision Maker"}
                          </Badge>
                          <Button
                            variant={user.isActive ? "destructive" : "default"}
                            size="sm"
                            onClick={() =>
                              handleStatusToggle(user.id, user.isActive)
                            }
                            disabled={updateUserStatusMutation.isPending}
                          >
                            {user.isActive ? (
                              <>
                                <XCircle className="mr-1 h-3 w-3" />
                                Deactivate
                              </>
                            ) : (
                              <>
                                <CheckCircle className="mr-1 h-3 w-3" />
                                Activate
                              </>
                            )}
                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() =>
                              handleDeleteUser(
                                user.id,
                                `${user.firstName} ${user.lastName}`,
                              )
                            }
                            disabled={deleteUserMutation.isPending}
                            className="text-red-600 border-red-300 hover:bg-red-50"
                          >
                            <Trash2 className="mr-1 h-3 w-3" />
                            Delete
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <Users className="text-gray-300 mx-auto mb-4" size={48} />
                    <p className="text-gray-500">No company users found</p>
                    <p className="text-sm text-gray-400 mt-2">
                      Start by creating users for your verified domain
                    </p>
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Team Management Tab */}
          <TabsContent value="team">
            <TeamManagement />
          </TabsContent>

          {/* Credits Tab */}
          <TabsContent value="credits">
            <CreditsOverview />
          </TabsContent>

          {/* DM Tracking Tab */}
          <TabsContent value="dms">
            <DMTracking />
          </TabsContent>

          {/* Call Activity Log Tab */}
          <TabsContent value="calls">
            <CallActivityLog />
          </TabsContent>

          {/* Performance Analytics Tab */}
          <TabsContent value="performance">
            <PerformanceAnalytics />
          </TabsContent>

          {/* Account Settings Tab */}
          <TabsContent value="settings">
            <AccountSettings />
          </TabsContent>

          {/* Analytics Tab */}
          <TabsContent value="analytics">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <BarChart3 className="text-blue-500 mr-3" size={20} />
                    User Growth
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between">
                      <span>Sales Representatives</span>
                      <span className="font-medium">
                        {analytics?.salesReps || 0}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span>Decision Makers</span>
                      <span className="font-medium">
                        {analytics?.decisionMakers || 0}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span>Active Users</span>
                      <span className="font-medium">
                        {analytics?.activeUsers || 0}
                      </span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <Activity className="text-green-500 mr-3" size={20} />
                    Platform Activity
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between">
                      <span>Total Invitations</span>
                      <span className="font-medium">
                        {analytics?.totalInvitations || 0}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span>Scheduled Meetings</span>
                      <span className="font-medium">
                        {analytics?.scheduledMeetings || 0}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span>Completion Rate</span>
                      <span className="font-medium">
                        {analytics?.completionRate || 0}%
                      </span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/flags-management.jsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import {
  Flag,
  AlertTriangle,
  CheckCircle,
  Clock,
  Plus,
  Eye,
  Filter,
  Search,
  X,
} from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function FlagsManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [selectedFlag, setSelectedFlag] = useState(null);
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [newFlag, setNewFlag] = useState({
    dmId: "",
    reason: "",
    description: "",
    priority: "medium",
    flagType: "behavior",
  });

  // Fetch flags based on user role
  const { data: flags = [], isLoading: flagsLoading } = useQuery({
    queryKey: ["/api/flags", user?.role],
    enabled: !!user?.id,
  });

  // Fetch available decision makers for flagging
  const { data: availableDMs = [] } = useQuery({
    queryKey: ["/api/calendar/available-dms"],
    enabled: !!user?.id && user?.role === "sales_rep",
  });

  // Create flag mutation
  const createFlagMutation = useMutation({
    mutationFn: async (flagData) => {
      return await apiRequest("POST", "/api/flags", {
        method: "POST",
        body: JSON.stringify(flagData),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/flags"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/flags-count"] });
      setShowCreateDialog(false);
      setNewFlag({
        dmId: "",
        reason: "",
        description: "",
        priority: "medium",
        flagType: "behavior",
      });
      toast({
        title: "Flag Created",
        description: "The flag has been successfully submitted for review.",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create flag",
        variant: "destructive",
      });
    },
  });

  // Update flag status mutation
  const updateFlagMutation = useMutation({
    mutationFn: async ({ flagId, status, resolution }) => {
      return await apiRequest("PUT", `/api/flags/${flagId}`, {
        method: "PUT",
        body: JSON.stringify({ status, resolution }),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/flags"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/flags-count"] });
      setSelectedFlag(null);
      toast({
        title: "Flag Updated",
        description: "The flag status has been updated successfully.",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update flag",
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status) => {
    switch (status) {
      case "open":
        return "bg-red-100 text-red-800";
      case "pending":
        return "bg-yellow-100 text-yellow-800";
      case "resolved":
        return "bg-green-100 text-green-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case "high":
        return "bg-red-100 text-red-800 border-red-200";
      case "medium":
        return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case "low":
        return "bg-blue-100 text-blue-800 border-blue-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  const filteredFlags = flags.filter((flag) => {
    const matchesSearch =
      flag.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      flag.reason?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus =
      statusFilter === "all" || flag.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  if (flagsLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <Flag className="h-12 w-12 animate-pulse text-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading flags...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50 pt-16">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">
                Flags Management
              </h1>
              <p className="text-gray-600 mt-1">
                Monitor and manage system flags
              </p>
            </div>

          </div>
        </div>

        {/* Filters */}
        <div className="mb-6 flex flex-col sm:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search
                className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"
                size={16}
              />
              <Input
                placeholder="Search flags..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>
          </div>
          <Select value={statusFilter} onValueChange={setStatusFilter}>
            <SelectTrigger className="w-full sm:w-[180px]">
              <SelectValue placeholder="Filter by status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Status</SelectItem>
              <SelectItem value="open">Open</SelectItem>
              <SelectItem value="pending">Pending</SelectItem>
              <SelectItem value="resolved">Resolved</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Flags List */}
        <div className="grid gap-4">
          {filteredFlags.length === 0 ? (
            <Card>
              <CardContent className="p-8 text-center">
                <Flag className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  No flags found
                </h3>
                <p className="text-gray-600">
                  {flags.length === 0
                    ? "No flags have been created yet."
                    : "No flags match your current filters."}
                </p>
              </CardContent>
            </Card>
          ) : (
            filteredFlags.map((flag) => (
              <Card key={flag.id} className="hover:shadow-md transition-shadow">
                <CardContent className="p-6">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-3">
                        <Badge className={getStatusColor(flag.status)}>
                          {flag.status === "open" && (
                            <AlertTriangle className="mr-1" size={12} />
                          )}
                          {flag.status === "pending" && (
                            <Clock className="mr-1" size={12} />
                          )}
                          {flag.status === "resolved" && (
                            <CheckCircle className="mr-1" size={12} />
                          )}
                          {flag.status
                            ? flag.status.charAt(0).toUpperCase() +
                              flag.status.slice(1)
                            : "Unknown"}
                        </Badge>
                        <Badge
                          variant="outline"
                          className={getPriorityColor(
                            flag.priority || "medium",
                          )}
                        >
                          {flag.priority
                            ? flag.priority.charAt(0).toUpperCase() +
                              flag.priority.slice(1)
                            : "Medium"}{" "}
                          Priority
                        </Badge>
                        <span className="text-sm text-gray-500">
                          {new Date(flag.reportedAt).toLocaleDateString()}
                        </span>
                      </div>
                      <h3 className="font-semibold text-gray-900 mb-2">
                        {flag.reason
                          ? flag.reason
                              .replace(/_/g, " ")
                              .replace(/\b\w/g, (l) => l.toUpperCase())
                          : "No reason provided"}
                      </h3>
                      <p className="text-gray-600 mb-3">{flag.description}</p>
                      <div className="text-sm text-gray-500">
                        <p>
                          Flagged by: {flag.flaggedByRole?.replace("_", " ")} ‚Ä¢
                          Target: Decision Maker
                        </p>
                        {flag.resolvedAt && (
                          <p>
                            Resolved:{" "}
                            {new Date(flag.resolvedAt).toLocaleDateString()}
                          </p>
                        )}
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setSelectedFlag(flag)}
                      >
                        <Eye className="mr-1" size={14} />
                        View
                      </Button>
                      {(user?.role === "enterprise_admin" ||
                        user?.role === "super_admin") &&
                        flag.status !== "resolved" && (
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() =>
                              updateFlagMutation.mutate({
                                flagId: flag.id,
                                status: "resolved",
                                resolution: "Resolved by admin",
                              })
                            }
                          >
                            <CheckCircle className="mr-1" size={14} />
                            Resolve
                          </Button>
                        )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </div>

        {/* Flag Details Dialog */}
        {selectedFlag && (
          <Dialog
            open={!!selectedFlag}
            onOpenChange={() => setSelectedFlag(null)}
          >
            <DialogContent className="sm:max-w-[500px]">
              <DialogHeader>
                <DialogTitle className="flex items-center gap-2">
                  <Flag className="h-5 w-5" />
                  Flag Details
                </DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div className="flex gap-2">
                  <Badge
                    className={getStatusColor(selectedFlag.status || "open")}
                  >
                    {selectedFlag.status
                      ? selectedFlag.status.charAt(0).toUpperCase() +
                        selectedFlag.status.slice(1)
                      : "Unknown"}
                  </Badge>
                  <Badge
                    variant="outline"
                    className={getPriorityColor(
                      selectedFlag.priority || "medium",
                    )}
                  >
                    {selectedFlag.priority
                      ? selectedFlag.priority.charAt(0).toUpperCase() +
                        selectedFlag.priority.slice(1)
                      : "Medium"}{" "}
                    Priority
                  </Badge>
                </div>
                <div>
                  <Label className="text-sm font-medium text-gray-700">
                    Reason
                  </Label>
                  <p className="text-gray-900">
                    {selectedFlag.reason
                      ? selectedFlag.reason
                          .replace(/_/g, " ")
                          .replace(/\b\w/g, (l) => l.toUpperCase())
                      : "No reason provided"}
                  </p>
                </div>
                <div>
                  <Label className="text-sm font-medium text-gray-700">
                    Description
                  </Label>
                  <p className="text-gray-900">{selectedFlag.description}</p>
                </div>
                <div>
                  <Label className="text-sm font-medium text-gray-700">
                    Reported
                  </Label>
                  <p className="text-gray-900">
                    {new Date(selectedFlag.reportedAt).toLocaleString()}
                  </p>
                </div>
                {selectedFlag.resolution && (
                  <div>
                    <Label className="text-sm font-medium text-gray-700">
                      Resolution
                    </Label>
                    <p className="text-gray-900">{selectedFlag.resolution}</p>
                  </div>
                )}
              </div>
              <DialogFooter>
                <Button variant="outline" onClick={() => setSelectedFlag(null)}>
                  Close
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        )}
      </div>
    </div>
  );
}

client/src/pages/landing.jsx.content:
import { Link, useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import {
  UserCheck,
  Calendar,
  TrendingUp,
  Shield,
  Mail,
  Users,
  Check,
  ArrowRight,
  Loader2,
} from "lucide-react";

export default function Landing() {
  const { user, isAuthenticated } = useAuth();
  const [, setLocation] = useLocation();

  // Auto-redirect authenticated users to their dashboard
  useEffect(() => {
    if (isAuthenticated && user) {
      switch (user.role) {
        case "sales_rep":
          setLocation("/sales-dashboard");
          break;
        case "decision_maker":
          setLocation("/decision-dashboard");
          break;
        case "enterprise_admin":
          setLocation("/enterprise-admin");
          break;
        case "admin":
          setLocation("/admin");
          break;
        case "super_admin":
          setLocation("/super-admin/dashboard");
          break;
        default:
          // Stay on landing page for unknown roles
          break;
      }
    }
  }, [isAuthenticated, user, setLocation]);

  // Fetch subscription plans from public endpoint
  const {
    data: subscriptionPlans,
    isLoading: plansLoading,
    error: plansError,
  } = useQuery({
    queryKey: ["/api/subscription-plans"],
    retry: false,
  });

  const features = [
    {
      icon: UserCheck,
      title: "Verified Decision-Makers",
      description:
        "Access a growing database of verified executives and decision-makers",
      color: "blue",
    },
    {
      icon: Calendar,
      title: "Smart Scheduling",
      description:
        "Automated 15-minute intro calls with integrated calendar booking",
      color: "blue",
    },
    {
      icon: TrendingUp,
      title: "AI-Powered Insights",
      description:
        "Custom call preparation and performance analysis for better outcomes",
      color: "green",
    },
    {
      icon: Shield,
      title: "Quality Assurance",
      description:
        "Traffic light validation system ensures top-quality experiences",
      color: "indigo",
    },
    {
      icon: Mail,
      title: "Email Access",
      description:
        "Premium access to decision-maker email addresses with approval",
      color: "yellow",
    },
    {
      icon: Users,
      title: "Pro Teams",
      description:
        "Enterprise solutions for entire sales teams with advanced guides",
      color: "lightblue",
    },
  ];

  // Helper function to format plan data for display
  const formatPlanForDisplay = (plan) => {
    // Extract numeric value from price string (e.g., "$0" -> 0, "$29" -> 29)
    const numericPrice =
      typeof plan.price === "string"
        ? parseFloat(plan.price.replace(/[^0-9.]/g, ""))
        : plan.price;

    const price = numericPrice === 0 ? "Free" : `$${numericPrice}`;
    const period =
      plan.billingInterval && numericPrice > 0
        ? `/${plan.billingInterval === "monthly" ? "month" : plan.billingInterval}`
        : numericPrice > 0
          ? "/month"
          : "";

    // Build comprehensive features list matching signup pages
    const baseFeatures =
      plan.features && plan.features.length > 0 ? plan.features : [];

    // Add structured features based on plan capabilities
    const structuredFeatures = [
      `${plan.maxInvitations || 0} decision maker connections`,
      `${plan.maxCallCredits || 0} call credits per month`,
      plan.prioritySupport
        ? "Priority support & assistance"
        : "Standard email support",
      "LinkedIn profile verification",
      "Calendar integration & scheduling",
      "Quality assurance system",
    ];

    // Add premium features based on plan tier
    // if (numericPrice > 0) {
    //   structuredFeatures.push("Advanced analytics dashboard");
    //   structuredFeatures.push("Email notifications");
    //   structuredFeatures.push("Call recording access");
    // }

    // if (numericPrice >= 39) {
    //   structuredFeatures.push("CRM integrations");
    //   structuredFeatures.push("Executive networking events");
    //   structuredFeatures.push("Performance insights");
    //   structuredFeatures.push("Custom availability settings");
    // }

    // if (numericPrice >= 59) {
    //   structuredFeatures.push("Team management tools");
    //   structuredFeatures.push("White-label options");
    //   structuredFeatures.push("Dedicated account manager");
    //   structuredFeatures.push("Enterprise security features");
    // }

    // Combine custom features with structured features
    const allFeatures = [...baseFeatures, ...structuredFeatures];

    return {
      id: plan.id,
      name: plan.name,
      price: price,
      period: period,
      bestSeller: plan.bestSeller || false,
      features: allFeatures,
      description:
        plan.description || `Perfect for ${plan.name.toLowerCase()} users`,
      maxCallCredits: plan.maxCallCredits,
      maxInvitations: plan.maxInvitations,
      prioritySupport: plan.prioritySupport,
      buttonText:
        numericPrice === 0
          ? "Get Started"
          : plan.name.toLowerCase().includes("team")
            ? "Contact Sales"
            : "Get Started",
      buttonVariant: plan.bestSeller ? "default" : "outline",
    };
  };

  const getIconColor = (color) => {
    const colors = {
      blue: "text-blue-600",
      lightblue: "text-blue-500",
      green: "text-green-600",
      indigo: "text-indigo-600",
      yellow: "text-yellow-600",
    };
    return colors[color] || "text-blue-600";
  };

  const getBgColor = (color) => {
    const colors = {
      blue: "bg-blue-100",
      lightblue: "bg-blue-50",
      green: "bg-green-100",
      indigo: "bg-indigo-100",
      yellow: "bg-yellow-100",
    };
    return colors[color] || "bg-blue-100";
  };

  return (
    <div className="min-h-screen pt-16">
      {/* Hero Section */}
      <section className="py-20 px-4">
        <div className="max-w-4xl mx-auto text-center">
          <h1 className="text-5xl md:text-6xl font-bold text-gray-900 mb-6">
            <span className="text-blue-600">Warm Intros</span>
            <br />
            to Decision-Makers
          </h1>
          <p className="text-xl text-gray-600 mb-10 max-w-2xl mx-auto leading-relaxed">
            Connect sales reps with verified executives through a
            community-driven platform. Contribute decision-makers, earn credits,
            book quality intro calls.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
            <Link href="/signup/sales-rep/personal-info">
              <Button
                size="lg"
                className="bg-blue-600 text-white px-8 py-4 text-lg font-semibold hover:bg-blue-700 transform hover:scale-105 transition-all shadow-lg"
              >
                I'm a Sales Rep <ArrowRight className="ml-2" size={20} />
              </Button>
            </Link>
            <Link href="/signup/decision-maker/personal-info">
              <Button
                variant="outline"
                size="lg"
                className="border-2 border-blue-600 text-blue-600 px-8 py-4 text-lg font-semibold hover:bg-blue-50"
              >
                I'm a Decision Maker ‚úì
              </Button>
            </Link>
          </div>
        </div>
      </section>

      {/* How Naeberly Works */}
      <section className="py-20 px-4 bg-white">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">
              How Naeborly Works
            </h2>
            <p className="text-xl text-gray-600">
              A contribution-based community where quality connections drive
              mutual success
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {features.map((feature, index) => {
              const IconComponent = feature.icon;
              return (
                <Card
                  key={index}
                  className="hover:shadow-xl transition-all border border-gray-100"
                >
                  <CardContent className="p-8">
                    <div
                      className={`w-12 h-12 ${getBgColor(feature.color)} rounded-xl flex items-center justify-center mb-6`}
                    >
                      <IconComponent
                        className={`${getIconColor(feature.color)} text-xl`}
                        size={24}
                      />
                    </div>
                    <h3 className="text-xl font-bold text-gray-900 mb-4">
                      {feature.title}
                    </h3>
                    <p className="text-gray-600 leading-relaxed">
                      {feature.description}
                    </p>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        </div>
      </section>

      {/* Pricing Section */}
      <section className="py-20 px-4 bg-gradient-to-br from-blue-50 to-slate-50">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">
              Choose Your Plan
            </h2>
            <p className="text-xl text-gray-600">
              Flexible options for individuals and teams
            </p>
          </div>

          {plansLoading ? (
            <div className="flex justify-center items-center py-16">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
              <span className="ml-3 text-gray-600">
                Loading pricing plans...
              </span>
            </div>
          ) : plansError ? (
            <div className="text-center py-16">
              <p className="text-gray-500">
                Unable to load pricing plans. Please try again later.
              </p>
            </div>
          ) : (
            <div className="grid md:grid-cols-3 gap-8 max-w-4xl mx-auto">
              {subscriptionPlans?.map((planData, index) => {
                const plan = formatPlanForDisplay(planData);
                return (
                  <Card
                    key={plan.id || index}
                    className={`relative ${plan.bestSeller ? "border-2 border-blue-600 shadow-xl" : "border border-gray-200 shadow-lg"}`}
                  >
                    {plan.bestSeller && (
                      <div className="absolute -top-4 left-1/2 transform -translate-x-1/2">
                        <span className="bg-blue-600 text-white px-4 py-1 rounded-full text-sm font-semibold">
                          Best Seller
                        </span>
                      </div>
                    )}
                    <CardContent className="p-8">
                      <div className="text-center mb-8">
                        <h3 className="text-2xl font-bold text-gray-900 mb-2">
                          {plan.name}
                        </h3>
                        <div className="text-4xl font-bold text-blue-600 mb-1">
                          {plan.price}
                        </div>
                        {plan.period && (
                          <div className="text-gray-500">{plan.period}</div>
                        )}
                        {plan.description &&
                          plan.description !== plan.features[0] && (
                            <p className="text-sm text-gray-600 mt-3 italic">
                              {plan.description}
                            </p>
                          )}
                      </div>

                      {/* Features List */}
                      <div className="mb-8">
                        <ul className="space-y-3">
                          {plan.features.map((feature, featureIndex) => (
                            <li
                              key={featureIndex}
                              className="flex items-start text-gray-600"
                            >
                              <Check
                                className="text-green-600 mr-3 mt-0.5 flex-shrink-0"
                                size={16}
                              />
                              <span className="text-sm leading-relaxed">
                                {feature}
                              </span>
                            </li>
                          ))}
                        </ul>

                        {/* Additional Plan Details */}
                        {(plan.maxCallCredits !== undefined ||
                          plan.maxInvitations !== undefined ||
                          plan.prioritySupport) && (
                          <div className="mt-6 pt-4 border-t border-gray-100">
                            <div className="grid grid-cols-1 gap-2 text-xs text-gray-500">
                              {plan.maxCallCredits !== undefined && (
                                <div className="flex justify-between">
                                  <span>Call Credits:</span>
                                  <span className="font-medium">
                                    {plan.maxCallCredits === -1
                                      ? "Unlimited"
                                      : plan.maxCallCredits}
                                  </span>
                                </div>
                              )}
                              {plan.maxInvitations !== undefined && (
                                <div className="flex justify-between">
                                  <span>Invitations:</span>
                                  <span className="font-medium">
                                    {plan.maxInvitations === -1
                                      ? "Unlimited"
                                      : plan.maxInvitations}
                                  </span>
                                </div>
                              )}
                              {plan.prioritySupport && (
                                <div className="flex justify-between">
                                  <span>Support:</span>
                                  <span className="font-medium text-blue-600">
                                    Priority
                                  </span>
                                </div>
                              )}
                            </div>
                          </div>
                        )}
                      </div>

                      <Button
                        variant={plan.buttonVariant}
                        className={`w-full py-3 font-semibold transition-all ${
                          plan.bestSeller
                            ? "bg-blue-600 hover:bg-blue-700 text-white shadow-lg hover:shadow-xl"
                            : plan.name.toLowerCase().includes("team")
                              ? "border-blue-600 text-blue-600 hover:bg-blue-50"
                              : "border-gray-300 text-gray-700 hover:bg-gray-50"
                        }`}
                      >
                        {plan.buttonText}
                      </Button>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 px-4 bg-gradient-to-r from-blue-600 to-blue-700">
        <div className="max-w-4xl mx-auto text-center">
          <h2 className="text-4xl font-bold text-white mb-6">
            Ready to Start Connecting?
          </h2>
          <p className="text-xl text-blue-100 mb-10">
            Join the community of sales professionals and decision-makers
            building meaningful business relationships
          </p>
          <Link href="/sales-dashboard">
            <Button
              size="lg"
              className="bg-white text-blue-600 px-8 py-4 text-lg font-bold hover:bg-gray-100 transform hover:scale-105 transition-all shadow-lg"
            >
              Get Started as Sales Rep
            </Button>
          </Link>
        </div>
      </section>

      {/* Footer with Admin Access */}
      <footer className="bg-gray-900 text-white py-8">
        <div className="max-w-6xl mx-auto px-4 text-center">
          <div className="flex justify-center items-center space-x-8">
            <p className="text-gray-400">
              ¬© 2024 Naeborly. All rights reserved.
            </p>
            <Link href="/super-admin/login">
              <Button
                variant="ghost"
                size="sm"
                className="text-gray-400 hover:text-white"
              >
                <Shield className="h-4 w-4 mr-2" />
                Admin Access
              </Button>
            </Link>
          </div>
        </div>
      </footer>
    </div>
  );
}

client/src/pages/login.jsx.content:
import { useState } from "react";
import { Link, useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Handshake, Eye, EyeOff, ArrowLeft } from "lucide-react";

const loginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(1, "Password is required"),
});

export default function Login() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [showPassword, setShowPassword] = useState(false);

  const form = useForm({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const loginMutation = useMutation({
    mutationFn: async (data) => {
      return await apiRequest("/api/login", {
        method: "POST",
        body: JSON.stringify(data),
      });
    },
    onSuccess: (response) => {
      console.log("Login successful:", response);

      // Store JWT token first
      if (response.token) {
        localStorage.setItem("naeborly_token", response.token);
        // Trigger storage event for other components
        window.dispatchEvent(new Event("storage"));
      }

      toast({
        title: "Welcome back!",
        description: "Login successful",
      });

      // Navigate based on user role with proper delay
      setTimeout(() => {
        // Clear queries after token is stored
        queryClient.clear();

        if (response.user.role === "sales_rep") {
          setLocation("/sales-dashboard");
        } else if (response.user.role === "decision_maker") {
          setLocation("/decision-dashboard");
        } else if (response.user.role === "enterprise_admin") {
          setLocation("/enterprise-admin");
        } else {
          setLocation("/");
        }
      }, 200);
    },
    onError: (error) => {
      console.error("Login error:", error);

      // Handle suspended account differently
      if (error.suspended) {
        toast({
          title: "Account Suspended",
          description: error.message,
          variant: "destructive",
          duration: 8000, // Show longer for suspension messages
        });
      } else {
        toast({
          title: "Login Failed",
          description: error.message || "Invalid email or password",
          variant: "destructive",
        });
      }
    },
  });

  const onSubmit = (data) => {
    console.log("Login form submission:", data);
    loginMutation.mutate(data);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white flex items-center justify-center p-4 pt-20">
      <div className="w-full max-w-md">
        <div className="text-center mb-8">
          <Link href="/" className="inline-flex items-center space-x-2 mb-6">
            <div className="w-10 h-10 bg-gradient-to-r from-blue-600 to-blue-700 rounded-lg flex items-center justify-center">
              <Handshake className="text-white" size={20} />
            </div>
            <span className="text-2xl font-bold text-gray-900">Naeborly</span>
          </Link>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Welcome Back
          </h1>
          <p className="text-gray-600">Sign in to your account to continue</p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Sign In</CardTitle>
            <CardDescription>
              Enter your credentials to access your dashboard
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form
                onSubmit={form.handleSubmit(onSubmit)}
                className="space-y-4"
              >
                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email Address</FormLabel>
                      <FormControl>
                        <Input
                          type="email"
                          placeholder="Enter your email"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input
                            type={showPassword ? "text" : "password"}
                            placeholder="Enter your password"
                            {...field}
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                            onClick={() => setShowPassword(!showPassword)}
                          >
                            {showPassword ? (
                              <EyeOff className="h-4 w-4" />
                            ) : (
                              <Eye className="h-4 w-4" />
                            )}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full bg-blue-600 hover:bg-blue-700"
                  disabled={loginMutation.isPending}
                >
                  {loginMutation.isPending ? "Signing In..." : "Sign In"}
                </Button>
              </form>
            </Form>

            <div className="mt-6 text-center space-y-4">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-gray-300" />
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-white text-gray-500">
                    Don't have an account?
                  </span>
                </div>
              </div>

              <div className="grid grid-cols-1 gap-3">
                <Link href="/signup/sales-rep/personal-info">
                  <Button variant="outline" className="w-full">
                    Sign up as Sales Rep
                  </Button>
                </Link>
                <Link href="/signup/decision-maker/personal-info">
                  <Button variant="outline" className="w-full">
                    Sign up as Decision Maker
                  </Button>
                </Link>
              </div>
            </div>
          </CardContent>
        </Card>

        <div className="mt-6 text-center">
          <Link href="/">
            <Button
              variant="ghost"
              className="text-gray-600 hover:text-purple-600"
            >
              <ArrowLeft className="mr-2" size={16} />
              Back to Home
            </Button>
          </Link>
        </div>
      </div>
    </div>
  );
}

