        if (company.id === companyId) {
          const newRemaining = company.remaining + parseInt(additionalCredits);
          const newAllocated = company.allocated + parseInt(additionalCredits);
          return {
            ...company,
            remaining: newRemaining,
            allocated: newAllocated,
            remainingColor:
              newRemaining > 100
                ? "text-green-600"
                : newRemaining > 50
                  ? "text-orange-600"
                  : "text-red-600",
          };
        }
        return company;
      }),
    );
  };

