import { useState } from "react";
import { useRoute, useLocation } from "wouter";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { CheckCircle, XCircle, MessageSquare } from "lucide-react";

export default function DMFeedback() {
  const [, params] = useRoute("/feedback/dm/:callId");
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const callId = params?.callId;

  const [formData, setFormData] = useState({
    callTookPlace: "",
    wasPoliteEngaged: "",
    comments: ""
  });

  const submitInitialFeedbackMutation = useMutation({
    mutationFn: async (data) => {
      return await apiRequest(`/api/decision-maker/initial-feedback/${callId}`, {
        method: "POST",
        body: JSON.stringify(data)
      });
    },
    onSuccess: (data) => {
      if (data.proceedToRating) {
        // Proceed to rep evaluation page for decision maker
        setLocation(`/evaluation/rep?callId=${callId}`);
      } else {
        // Show success message and redirect to dashboard
        toast({
          title: "Feedback Submitted",
          description: "Thank you for your feedback. Your input helps us improve the platform.",
        });
        setLocation("/decision-dashboard");
      }
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to submit feedback. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.callTookPlace || !formData.wasPoliteEngaged) {
      toast({
        title: "Missing Information",
        description: "Please answer both required questions.",
        variant: "destructive",
      });
      return;
    }

    submitInitialFeedbackMutation.mutate(formData);
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  if (!callId) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <Card className="w-full max-w-md">
          <CardContent className="pt-6">
            <div className="text-center">
              <XCircle className="h-12 w-12 text-red-500 mx-auto mb-4" />
              <h2 className="text-lg font-semibold mb-2">Invalid Feedback Link</h2>
              <p className="text-gray-600 mb-4">This feedback link is not valid or has expired.</p>
              <Button onClick={() => setLocation("/decision-dashboard")}>
                Go to Dashboard
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-slate-50 py-8">
      <div className="max-w-2xl mx-auto px-4">
        <Card className="shadow-lg">
          <CardHeader className="text-center bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-t-lg">
            <CardTitle className="text-2xl font-bold flex items-center justify-center gap-2">
              <MessageSquare className="h-6 w-6" />
              Quick Feedback
            </CardTitle>
