import React, { useState } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormMessage,
} from "@/components/ui/form";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import {
  ArrowLeft,
  Check,
  Loader2,
  Star,
  Shield,
  CreditCard,
  Zap,
  Users,
  Calendar,
} from "lucide-react";
import { decisionMakerPackageSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import StripeCheckout from "@/components/StripeCheckout";
import StripePaymentForm from "@/components/StripePaymentForm";

export default function DecisionMakerChoosePackage() {
  const [, setLocation] = useLocation();
  const [showPayment, setShowPayment] = useState(false);
  const [selectedPackage, setSelectedPackage] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const { toast } = useToast();

  // Get user info from localStorage or create test data
  const getCurrentUserInfo = () => {
    // In a real app, this would come from the signup session
    const testUserInfo = {
      email: "mlp.yashvantgupknkta@gmail.com", // Use the actual email from console logs
      firstName: "Yashwant",
      lastName: "Sahu",
    };
    return testUserInfo;
  };

  const form = useForm({
    resolver: zodResolver(decisionMakerPackageSchema),
    defaultValues: {
      packageType: "",
    },
  });

  const selectedValue = form.watch("packageType");

  const savePackageMutation = useMutation({
    mutationFn: async (data) => {
      console.log("Submitting decision maker package data:", data);
      const response = await apiRequest("/api/decision-maker/package", {
        method: "POST",
        body: JSON.stringify(data),
      });
      return response;
    },
    onSuccess: (data) => {
      console.log("Decision maker package selection saved successfully:", data);
      toast({
        title: "Account Created Successfully!",
        description:
          "Welcome to Naeberly! Your decision maker account is now ready.",
      });
      setLocation("/signup/decision-maker/professional-info");
    },
    onError: (error) => {
      console.error("Decision maker package save error:", error);
      toast({
        title: "Save Failed",
        description:
          error.message || "Failed to complete signup. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data) => {
    const selectedPkg = packages.find((pkg) => pkg.id === data.packageType);

    if (selectedPkg?.requiresPayment) {
      const userData = getCurrentUserInfo();
      setSelectedPackage(selectedPkg);
      setUserInfo({
        packageType: data.packageType,
        email: userData.email,
        firstName: userData.firstName,
        lastName: userData.lastName,
      });
      setShowPayment(true);
    } else {
      // Free package - proceed directly
      savePackageMutation.mutate({
        packageType: data.packageType, // Use MongoDB ID for backend
      });
    }
  };

  const handlePaymentSuccess = () => {
    // Complete the signup after payment
    savePackageMutation.mutate({
      packageType: selectedPackage.id, // Use MongoDB ID for backend
    });
  };

  const handlePaymentError = (error) => {
    toast({
      title: "Payment Failed",
      description:
        error?.message || "Payment could not be processed. Please try again.",
      variant: "destructive",
    });
    setShowPayment(false);
  };

  // Fetch subscription plans from API
  const { data: subscriptionPlans, isLoading: plansLoading } = useQuery({
    queryKey: ["/api/subscription-plans"],
    queryFn: async () => {
      const response = await apiRequest("/api/subscription-plans");
      return response;
    },
  });

  // Helper functions for enhanced UI
  function getPackageIcon(planName) {
    switch (planName?.toLowerCase()) {
      case "free":
        return Star;
      case "basic":
        return Zap;
      case "premium":
        return Shield;
      case "enterprise":
        return Users;
      default:
        return CreditCard;
    }
  }

  function getPackageGradient(planName) {
    switch (planName?.toLowerCase()) {
      case "free":
        return "from-green-50 to-emerald-100";
      case "basic":
        return "from-blue-50 to-indigo-100";
      case "premium":
        return "from-purple-50 to-pink-100";
      case "enterprise":
        return "from-orange-50 to-red-100";
      default:
        return "from-gray-50 to-gray-100";
    }
  }

  // Helper function to map plan names to schema enum values
  function getEnumValue(planName) {
    switch (planName?.toLowerCase()) {
      case "free":
        return "free";
      case "basic":
        return "basic";
      case "premium":
        return "premium";
      case "enterprise":
        return "premium"; // Map enterprise to premium for DM
      case "pro-team":
        return "premium"; // Map pro-team to premium for DM
      default:
        return "free";
    }
  }

  // Transform API data to match component structure
  const packages =
    subscriptionPlans?.map((plan) => {
      // Build comprehensive features list from plan data
      const baseFeatures =
        plan.features && plan.features.length > 0 ? plan.features : [];

      // Add structured features based on plan capabilities
      const structuredFeatures = [
        `Up to ${plan.maxCallCredits || 0} scheduled calls per month`,
        `${plan.maxInvitations || 0} sales rep connections`,
        plan.prioritySupport
          ? "Priority support & assistance"
          : "Standard email support",
        "Verified sales rep network access",
        "Flexible scheduling preferences",
        "Call quality assurance",
      ];

      // Add premium features based on plan tier
      if (plan.price > 0) {
        structuredFeatures.push("Advanced call analytics");
        structuredFeatures.push("LinkedIn integration");
        structuredFeatures.push("Calendar sync");
      }

      if (plan.price >= 39) {
        structuredFeatures.push("Call recording access");
        structuredFeatures.push("Executive networking events");
        structuredFeatures.push("Performance insights");
        structuredFeatures.push("Custom availability settings");
      }

      if (plan.price >= 59) {
        structuredFeatures.push("Team coordination tools");
        structuredFeatures.push("Bulk call scheduling");
        structuredFeatures.push("Enterprise security features");
        structuredFeatures.push("Dedicated account manager");
      }

      // Combine custom features with structured features
      const allFeatures = [...baseFeatures, ...structuredFeatures];

      const enumValue = getEnumValue(plan.name);

      return {
        id: plan.id, // Use unique MongoDB ID for selection
        enumValue: enumValue, // Keep enum value for backend
        name: plan.name,
        price: plan.price?.toString() || "0",
        displayPrice:
          plan.price === 0 || plan.price === "0" ? "Free" : `$${plan.price}`,
        period:
          plan.billingInterval === "monthly"
            ? "month"
            : plan.billingPeriod || "month",
        description:
          plan.description ||
          `Perfect for ${plan.name.toLowerCase()} decision makers`,
        popular: plan.bestSeller || plan.featured || false,
        features: allFeatures,
        requiresPayment: plan.price > 0,
        stripePriceId: plan.stripePriceId,
        icon: getPackageIcon(plan.name),
        gradient: getPackageGradient(plan.name),
      };
    }) || [];

  // Set default to first available package when plans load
  React.useEffect(() => {
    if (packages && packages.length > 0 && !selectedValue) {
      const freePackage =
        packages.find((pkg) => pkg.enumValue === "free") || packages[0];
      form.setValue("packageType", freePackage.id);
    }
  }, [packages, selectedValue, form]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-slate-50">
      <div className="max-w-4xl mx-auto mt-16 px-4 py-8">
        {/* Progress Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <span className="text-sm font-medium text-gray-600">
              Step 2 of 4
            </span>
            <span className="text-sm font-medium text-gray-600">
              50% Complete
            </span>
          </div>
          <Progress value={50} className="h-2" />
        </div>

        {/* Main Content */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Choose Your Plan
          </h1>
          <p className="text-gray-600">
            Select the plan that best fits your networking needs
          </p>
        </div>

        {/* Benefits Banner */}
        <div className="bg-gradient-to-r from-purple-600 to-blue-600 text-white rounded-lg p-6 mb-8">
          <div className="flex items-center justify-center space-x-8">
            <div className="flex items-center space-x-2">
              <Shield className="h-5 w-5" />
              <span className="text-sm font-medium">
                Verified Sales Reps Only
              </span>
            </div>
            <div className="flex items-center space-x-2">
              <Check className="h-5 w-5" />
              <span className="text-sm font-medium">Quality Guaranteed</span>
            </div>
            <div className="flex items-center space-x-2">
              <Star className="h-5 w-5" />
              <span className="text-sm font-medium">Premium Networking</span>
            </div>
          </div>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)}>
            <FormField
              control={form.control}
              name="packageType"
              render={({ field }) => (
                <FormItem>
                  <FormControl>
                    <RadioGroup
                      onValueChange={field.onChange}
                      value={field.value}
                      className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-8 mb-8"
                    >
                      {plansLoading ? (
                        <div className="col-span-full flex justify-center py-12">
                          <div className="text-center">
                            <Loader2 className="h-12 w-12 animate-spin text-blue-600 mx-auto mb-4" />
                            <p className="text-gray-600">
                              Loading subscription plans...
                            </p>
                          </div>
                        </div>
                      ) : (
                        packages.map((pkg) => (
                          <div key={pkg.id} className="relative">
                            <RadioGroupItem
                              value={pkg.id}
                              id={pkg.id}
                              className="peer sr-only"
                            />
                            <label
                              htmlFor={pkg.id}
                              className="flex flex-col cursor-pointer"
                            >
                              <Card
                                className={`peer-checked:ring-4 peer-checked:ring-purple-500 peer-checked:border-purple-500 peer-checked:shadow-2xl peer-checked:scale-105 hover:shadow-xl hover:scale-102 transition-all duration-300 h-full overflow-hidden ${selectedValue === pkg.id ? "ring-4 ring-purple-500 border-purple-500 shadow-2xl scale-105" : ""}`}
                              >
                                {/* Background Gradient */}
                                <div
                                  className={`absolute inset-0 bg-gradient-to-br ${pkg.gradient} opacity-10`}
                                />

                                <CardContent className="relative p-6 flex flex-col h-full">
                                  {pkg.popular && (
                                    <div className="absolute -top-3 left-1/2 transform -translate-x-1/2 z-10">
                                      <Badge className="bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow-lg flex items-center gap-1">
                                        <Star className="h-3 w-3" />
                                        Most Popular
                                      </Badge>
                                    </div>
                                  )}

                                  {/* Package Icon */}
                                  <div className="flex justify-center mb-4">
                                    <div
                                      className={`p-3 rounded-full bg-gradient-to-br ${pkg.gradient} shadow-lg`}
                                    >
                                      {pkg.icon && (
                                        <pkg.icon className="h-8 w-8 text-gray-700" />
                                      )}
                                    </div>
                                  </div>

                                  <div className="text-center mb-6">
                                    <h3 className="text-2xl font-bold text-gray-900 mb-2">
                                      {pkg.name}
                                    </h3>
                                    <div className="mb-3">
                                      <span className="text-4xl font-bold bg-gradient-to-r from-purple-600 to-pink-600 bg-clip-text text-transparent">
                                        {pkg.displayPrice}
                                      </span>
                                      {pkg.period !== "forever" && (
                                        <span className="text-gray-600 ml-1">
                                          /{pkg.period}
                                        </span>
                                      )}
                                    </div>
                                    <p className="text-gray-600 text-sm leading-relaxed">
                                      {pkg.description}
                                    </p>
                                  </div>

                                  <div className="flex-1">
                                    <ul className="space-y-3">
                                      {pkg.features.map((feature, index) => (
                                        <li
                                          key={index}
                                          className="flex items-start gap-3"
                                        >
                                          <div className="flex-shrink-0 mt-0.5">
                                            <Check className="h-4 w-4 text-green-500 bg-green-50 rounded-full p-0.5" />
                                          </div>
                                          <span className="text-sm text-gray-700 leading-relaxed">
                                            {feature}
                                          </span>
                                        </li>
                                      ))}
                                    </ul>
                                  </div>

                                  {selectedValue === pkg.id && (
                                    <div className="mt-6">
                                      <div className="text-center p-3 bg-gradient-to-r from-purple-50 to-pink-50 border border-purple-200 rounded-lg">
                                        <div className="flex items-center justify-center gap-2">
                                          <span className="text-sm font-semibold text-purple-700">
                                            Selected Plan
                                          </span>
                                        </div>
                                      </div>
                                    </div>
                                  )}
                                </CardContent>
                              </Card>
                            </label>
                          </div>
                        ))
                      )}
                    </RadioGroup>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Additional Benefits */}
            <div className="bg-gray-50 rounded-lg p-6 mb-8">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 text-center">
                What You Get with Every Plan
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-gray-700">
                <div className="flex items-center space-x-2">
                  <Check className="h-4 w-4 text-green-500" />
                  <span>LinkedIn verification</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Check className="h-4 w-4 text-green-500" />
                  <span>Flexible scheduling</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Check className="h-4 w-4 text-green-500" />
                  <span>Quality assurance</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Check className="h-4 w-4 text-green-500" />
                  <span>Call recordings</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Check className="h-4 w-4 text-green-500" />
                  <span>Performance metrics</span>
                </div>
                <div className="flex items-center space-x-2">
                  <Check className="h-4 w-4 text-green-500" />
                  <span>Community access</span>
                </div>
              </div>
            </div>

            {/* Navigation Buttons */}
            <div className="flex justify-between pt-6">
              <Button
                type="button"
                variant="outline"
                onClick={() =>
                  setLocation("/signup/decision-maker/personal-info")
                }
                className="flex items-center gap-2"
              >
                <ArrowLeft className="h-4 w-4" />
                Back
              </Button>

              <Button
                type="submit"
                disabled={savePackageMutation.isPending}
                className="flex items-center gap-2 bg-purple-600 hover:bg-purple-700 px-8"
              >
                {savePackageMutation.isPending ? (
                  <Loader2 className="h-4 w-4 animate-spin" />
                ) : (
                  <>Complete Signup</>
                )}
              </Button>
            </div>
          </form>
        </Form>

        {/* Payment Modal */}
        {showPayment && selectedPackage && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="max-w-md w-full">
              <StripeCheckout
                packageInfo={selectedPackage}
                userInfo={userInfo}
                onSuccess={handlePaymentSuccess}
                onError={handlePaymentError}
                isRecurring={false}
              />
              <div className="mt-4 text-center">
                <Button
                  variant="outline"
                  onClick={() => setShowPayment(false)}
                  className="text-gray-600"
                >
                  Cancel Payment
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import { useState } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Progress } from "@/components/ui/progress";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import {
  ArrowLeft,
  Linkedin,
  CheckCircle,
  AlertCircle,
  Eye,
  EyeOff,
  Loader2,
} from "lucide-react";
import { decisionMakerPersonalInfoSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";

export default function DecisionMakerPersonalInfo() {
  const [, setLocation] = useLocation();
  const [linkedinVerified, setLinkedinVerified] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const { toast } = useToast();

  const form = useForm({
    resolver: zodResolver(decisionMakerPersonalInfoSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      email: "",
      companyDomain: "",
      linkedinUrl: "",
      password: "",
      confirmPassword: "",
    },
  });

  const linkedinVerifyMutation = useMutation({
    mutationFn: async (linkedinUrl) => {
      console.log(
        "Decision maker LinkedIn verification API call starting for:",
        linkedinUrl,
      );
      try {
        const response = await apiRequest("/api/verify-linkedin", {
          method: "POST",
          body: JSON.stringify({ linkedinUrl }),
        });
        console.log(
          "Decision maker LinkedIn verification API response:",
          response,
        );
        return response;
      } catch (error) {
        console.error("Decision maker LinkedIn verification API error:", error);
        throw error;
      }
    },
    onSuccess: (data) => {
      if (data.verified) {
        setLinkedinVerified(true);
        toast({
          title: "LinkedIn Verified",
          description:
            data.message ||
            "Your LinkedIn profile has been successfully verified.",
        });
      } else {
        toast({
          title: "Verification Failed",
          description: data.message || "Unable to verify LinkedIn profile.",
          variant: "destructive",
        });
      }
    },
    onError: (error) => {
      toast({
        title: "Verification Failed",
        description:
          error.message ||
          "Unable to verify LinkedIn profile. Please check the URL.",
        variant: "destructive",
      });
    },
  });

  const savePersonalInfoMutation = useMutation({
    mutationFn: async (data) => {
      console.log(
        "Submitting decision maker form data:",
        data,
        "LinkedIn verified:",
        linkedinVerified,
      );

      // Get invitation context from session storage
      const invitationContext = sessionStorage.getItem("invitationContext");
      let parsedInvitationContext = null;
      if (invitationContext) {
        try {
          parsedInvitationContext = JSON.parse(invitationContext);
          console.log("Including invitation context:", parsedInvitationContext);
        } catch (error) {
          console.error("Failed to parse invitation context:", error);
        }
      }

      const response = await apiRequest("/api/decision-maker/personal-info", {
        method: "POST",
        body: JSON.stringify({
          ...data,
          linkedinVerified,
          invitationContext: parsedInvitationContext,
        }),
      });
      return response;
    },
    onSuccess: (data) => {
      console.log("Decision maker personal info saved successfully:", data);
      toast({
        title: "Information Saved",
        description: "Your personal information has been saved successfully.",
      });
      setLocation("/signup/decision-maker/professional-info");
    },
    onError: (error) => {
      console.error("Decision maker form submission error:", error);
      toast({
        title: "Save Failed",
        description:
          error.message || "Failed to save information. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleLinkedinVerify = () => {
    const linkedinUrl = form.getValues("linkedinUrl");
    console.log("Decision maker LinkedIn verification attempt:", {
      linkedinUrl,
    });

    if (!linkedinUrl) {
      toast({
        title: "LinkedIn URL Required",
        description: "Please enter your LinkedIn profile URL first.",
        variant: "destructive",
      });
      return;
    }

    console.log(
      "Starting LinkedIn verification for decision maker:",
      linkedinUrl,
    );
    linkedinVerifyMutation.mutate(linkedinUrl);
  };

  const onSubmit = (data) => {
    console.log("Decision maker form submission attempt:", data);
    console.log("Form errors:", form.formState.errors);
    console.log("LinkedIn verified:", linkedinVerified);

    if (!linkedinVerified) {
      toast({
        title: "LinkedIn Verification Required",
        description: "Please verify your LinkedIn profile before proceeding.",
        variant: "destructive",
      });
      return;
    }
    savePersonalInfoMutation.mutate(data);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-slate-50">
      <div className="max-w-2xl mx-auto mt-16 px-4 py-8">
        {/* Progress Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <span className="text-sm font-medium text-gray-600">
              Step 1 of 4
            </span>
            <span className="text-sm font-medium text-gray-600">
              25% Complete
            </span>
          </div>
          <Progress value={25} className="h-2" />
        </div>

        {/* Main Form Card */}
        <Card className="shadow-lg">
          <CardContent className="p-8">
            <div className="text-center mb-8">
              <h1 className="text-3xl font-bold text-gray-900 mb-2">
                Personal Information
              </h1>
              <p className="text-gray-600">
                Let's start with your basic details and LinkedIn verification
              </p>
            </div>

            <Form {...form}>
              <form
                onSubmit={form.handleSubmit(onSubmit)}
                className="space-y-6"
              >
                {/* Name Fields */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="firstName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-sm font-medium text-gray-700">
                          First Name *
                        </FormLabel>
                        <FormControl>
                          <Input
                            placeholder="Enter your first name"
                            {...field}
                            className="mt-1"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="lastName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-sm font-medium text-gray-700">
                          Last Name *
                        </FormLabel>
                        <FormControl>
                          <Input
                            placeholder="Enter your last name"
                            {...field}
                            className="mt-1"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Email */}
                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">
                        Email Address *
                      </FormLabel>
                      <FormControl>
                        <Input
                          type="email"
                          placeholder="Enter your email address"
                          {...field}
                          className="mt-1"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Company Domain */}
                <FormField
                  control={form.control}
                  name="companyDomain"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">
