                            <Button
                              type="button"
                              variant="ghost"
                              size="sm"
                              className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                              onClick={() => setShowPassword(!showPassword)}
                            >
                              {showPassword ? (
                                <EyeOff className="h-4 w-4" />
                              ) : (
                                <Eye className="h-4 w-4" />
                              )}
                            </Button>
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="confirmPassword"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-sm font-medium text-gray-700">
                          Confirm Password *
                        </FormLabel>
                        <FormControl>
                          <div className="relative">
                            <Input
                              type={showConfirmPassword ? "text" : "password"}
                              placeholder="Confirm your password"
                              {...field}
                              className="mt-1 pr-10"
                            />
                            <Button
                              type="button"
                              variant="ghost"
                              size="sm"
                              className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                              onClick={() =>
                                setShowConfirmPassword(!showConfirmPassword)
                              }
                            >
                              {showConfirmPassword ? (
                                <EyeOff className="h-4 w-4" />
                              ) : (
                                <Eye className="h-4 w-4" />
                              )}
                            </Button>
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Password Requirements */}
                <div className="bg-gray-50 border border-gray-200 rounded-lg p-4">
                  <h4 className="text-sm font-medium text-gray-700 mb-2">
                    Password Requirements:
                  </h4>
                  <ul className="text-xs text-gray-600 space-y-1">
                    <li>‚Ä¢ At least 8 characters long</li>
                    <li>‚Ä¢ Contains uppercase and lowercase letters</li>
                    <li>‚Ä¢ Contains at least one number</li>
                    <li>‚Ä¢ Contains at least one special character</li>
                  </ul>
                </div>

                {/* Navigation */}
                <div className="flex justify-between items-center pt-6">
                  <Button
                    variant="ghost"
                    onClick={() => setLocation("/")}
                    className="text-gray-600"
                  >
                    <ArrowLeft className="mr-2" size={16} />
                    Back
                  </Button>
                  <Button
                    type="submit"
                    disabled={savePersonalInfoMutation.isPending}
                    className="bg-blue-600 hover:bg-purple-700 px-8"
                  >
                    {savePersonalInfoMutation.isPending ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Saving...
                      </>
                    ) : (
                      "Next"
                    )}
                  </Button>
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Progress } from "@/components/ui/progress";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { ArrowLeft, ArrowRight, Loader2 } from "lucide-react";
import { decisionMakerProfessionalSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";

export default function DecisionMakerProfessionalInfo() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const form = useForm({
    resolver: zodResolver(decisionMakerProfessionalSchema),
    defaultValues: {
      jobTitle: "",
      company: "",
      industry: "",
      companySize: "",
      yearsInRole: "",
    },
  });

  const saveProfessionalInfoMutation = useMutation({
    mutationFn: async (data) => {
      console.log("Submitting decision maker professional data:", data);
      const response = await apiRequest(
        "/api/decision-maker/professional-info",
        {
          method: "POST",
          body: JSON.stringify(data),
        },
      );
      return response;
    },
    onSuccess: (data) => {
      console.log("Decision maker professional info saved successfully:", data);
      toast({
        title: "Professional Information Saved",
        description:
          "Your professional background has been saved successfully.",
      });
      setLocation("/signup/decision-maker/availability");
    },
    onError: (error) => {
      console.error("Decision maker professional info save error:", error);
      toast({
        title: "Save Failed",
        description:
          error.message ||
          "Failed to save professional information. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data) => {
    saveProfessionalInfoMutation.mutate(data);
  };

  const industries = [
    "Technology",
    "Healthcare",
    "Finance",
    "Education",
    "Manufacturing",
    "Retail",
    "Real Estate",
    "Marketing & Advertising",
    "Consulting",
    "Energy",
    "Telecommunications",
    "Transportation",
    "Other",
  ];

  const companySizes = [
    "1-10 employees",
    "11-50 employees",
    "51-200 employees",
    "201-1000 employees",
    "1001-5000 employees",
    "5000+ employees",
  ];

  const yearsOptions = [
    "Less than 1 year",
    "1-2 years",
    "3-5 years",
    "6-10 years",
    "More than 10 years",
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-slate-50">
      <div className="max-w-2xl mx-auto mt-16 px-4 py-8">
        {/* Progress Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <span className="text-sm font-medium text-gray-600">
              Step 2 of 4
            </span>
            <span className="text-sm font-medium text-gray-600">
              50% Complete
            </span>
          </div>
          <Progress value={50} className="h-2" />
        </div>

        {/* Main Form Card */}
        <Card className="shadow-lg">
          <CardContent className="p-8">
            <div className="text-center mb-8">
              <h1 className="text-3xl font-bold text-gray-900 mb-2">
                Professional Background
              </h1>
              <p className="text-gray-600">
                Tell us about your professional experience and current role
              </p>
            </div>

            <Form {...form}>
              <form
                onSubmit={form.handleSubmit(onSubmit)}
                className="space-y-6"
              >
                {/* Job Title */}
                <FormField
                  control={form.control}
                  name="jobTitle"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">
                        Job Title *
                      </FormLabel>
                      <FormControl>
                        <Input
                          placeholder="e.g., Chief Technology Officer, VP of Sales"
                          {...field}
                          className="mt-1"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Company */}
                <FormField
                  control={form.control}
                  name="company"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">
                        Company *
                      </FormLabel>
                      <FormControl>
                        <Input
                          placeholder="Enter your company name"
                          {...field}
                          className="mt-1"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Industry */}
                <FormField
                  control={form.control}
                  name="industry"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">
                        Industry *
                      </FormLabel>
                      <Select
                        onValueChange={field.onChange}
                        defaultValue={field.value}
                      >
                        <FormControl>
                          <SelectTrigger className="mt-1">
                            <SelectValue placeholder="Select your industry" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {industries.map((industry) => (
                            <SelectItem key={industry} value={industry}>
                              {industry}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Company Size */}
                <FormField
                  control={form.control}
                  name="companySize"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">
                        Company Size *
                      </FormLabel>
                      <Select
                        onValueChange={field.onChange}
                        defaultValue={field.value}
                      >
                        <FormControl>
                          <SelectTrigger className="mt-1">
                            <SelectValue placeholder="Select company size" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {companySizes.map((size) => (
                            <SelectItem key={size} value={size}>
                              {size}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Years in Role */}
                <FormField
                  control={form.control}
                  name="yearsInRole"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">
                        Years in Current Role
                      </FormLabel>
                      <Select
                        onValueChange={field.onChange}
                        defaultValue={field.value}
                      >
                        <FormControl>
                          <SelectTrigger className="mt-1">
                            <SelectValue placeholder="Select years of experience" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {yearsOptions.map((years) => (
                            <SelectItem key={years} value={years}>
                              {years}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Navigation Buttons */}
                <div className="flex justify-between pt-6">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() =>
                      setLocation("/signup/decision-maker/personal-info")
                    }
                    className="flex items-center gap-2"
                  >
                    <ArrowLeft className="h-4 w-4" />
                    Back
                  </Button>

                  <Button
                    type="submit"
                    disabled={saveProfessionalInfoMutation.isPending}
                    className="flex items-center gap-2 bg-purple-600 hover:bg-purple-700"
                  >
                    {saveProfessionalInfoMutation.isPending ? (
                      <Loader2 className="h-4 w-4 animate-spin" />
                    ) : (
                      <>
                        Continue
                        <ArrowRight className="h-4 w-4" />
                      </>
                    )}
                  </Button>
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/signup/sales-rep/choose.content:
import React, { useState } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormMessage,
} from "@/components/ui/form";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import {
  ArrowLeft,
  Check,
  Loader2,
  Star,
  CreditCard,
  Zap,
  Users,
  Calendar,
  Shield,
} from "lucide-react";
import { salesRepPackageSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import StripeCheckout from "@/components/StripeCheckout";

export default function SalesRepChoosePackage() {
  const [, setLocation] = useLocation();
  const [showPayment, setShowPayment] = useState(false);
  const [selectedPackage, setSelectedPackage] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const { toast } = useToast();

  const form = useForm({
    resolver: zodResolver(salesRepPackageSchema),
    defaultValues: {
      packageType: "",
    },
  });

  const selectedValue = form.watch("packageType");

  const savePackageMutation = useMutation({
    mutationFn: async (data) => {
      console.log("Submitting package data:", data);
      const response = await apiRequest("/api/sales-rep/package", {
        method: "POST",
        body: JSON.stringify(data),
      });
      return response;
    },
    onSuccess: (data) => {
      console.log("Package selection saved successfully:", data);
      toast({
        title: "Account Created Successfully!",
        description:
          "Welcome to Naeborly! Your sales rep account is now ready.",
      });
      setLocation("/signup/sales-rep/professional-info");
    },
    onError: (error) => {
      console.error("Package save error:", error);
      toast({
        title: "Save Failed",
        description:
          error.message || "Failed to complete signup. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data) => {
    const selectedPkg = packages.find((pkg) => pkg.id === data.packageType);

    if (selectedPkg?.requiresPayment) {
      setSelectedPackage(selectedPkg);
      setUserInfo({
        packageType: data.packageType,
        email: "user@example.com", // This would come from previous signup steps
        firstName: "User",
        lastName: "Name",
      });
      setShowPayment(true);
    } else {
      // Free package - proceed directly
      savePackageMutation.mutate({
        packageType: data.packageType, // Use MongoDB ID for backend
      });
    }
  };

  const handlePaymentSuccess = () => {
    // Complete the signup after payment
    savePackageMutation.mutate({
      packageType: selectedPackage.id, // Use MongoDB ID for backend
    });
  };

  const handlePaymentError = (error) => {
    toast({
      title: "Payment Failed",
      description:
        error?.message || "Payment could not be processed. Please try again.",
      variant: "destructive",
    });
    setShowPayment(false);
  };

  // Fetch subscription plans from API
  const { data: subscriptionPlans, isLoading: plansLoading } = useQuery({
    queryKey: ["/api/subscription-plans"],
    queryFn: async () => {
      const response = await apiRequest("/api/subscription-plans");
      return response;
    },
  });

  // Helper functions for enhanced UI
  function getPackageIcon(planName) {
    switch (planName?.toLowerCase()) {
      case "free":
        return Star;
      case "basic":
        return Zap;
      case "premium":
        return Shield;
      case "pro-team":
        return Users;
      default:
        return CreditCard;
    }
  }

  function getPackageGradient(planName) {
    switch (planName?.toLowerCase()) {
      case "free":
        return "from-gray-50 to-gray-100";
      case "basic":
        return "from-blue-50 to-indigo-100";
      case "premium":
        return "from-purple-50 to-pink-100";
      case "pro-team":
        return "from-green-50 to-emerald-100";
      default:
        return "from-gray-50 to-gray-100";
    }
  }

  // Helper function to map plan names to schema enum values
  function getEnumValue(planName) {
    switch (planName?.toLowerCase()) {
      case "free":
        return "free";
      case "basic":
        return "basic";
      case "premium":
        return "premium";
      case "enterprise":
        return "pro-team";
      case "pro-team":
        return "pro-team";
      default:
        return "free";
    }
  }

  // Transform API data to match component structure
  const packages =
    subscriptionPlans?.map((plan) => {
      // Build comprehensive features list from plan data
      const baseFeatures =
        plan.features && plan.features.length > 0 ? plan.features : [];

      // Add structured features based on plan capabilities
      const structuredFeatures = [
        `${plan.maxInvitations || 0} decision maker invitations`,
        `${plan.maxCallCredits || 0} call credits per month`,
        plan.prioritySupport ? "Priority support" : "Standard support",
        "LinkedIn profile verification",
        "Calendar integration",
        "Basic analytics dashboard",
      ];

      // Add premium features based on plan tier
      if (plan.price > 0) {
        structuredFeatures.push("Advanced scheduling tools");
        structuredFeatures.push("Email notifications");
      }

      if (plan.price >= 39) {
        structuredFeatures.push("Call recording & transcription");
        structuredFeatures.push("CRM integrations");
        structuredFeatures.push("Advanced analytics");
      }

      if (plan.price >= 59) {
        structuredFeatures.push("Team management");
        structuredFeatures.push("White-label options");
        structuredFeatures.push("Dedicated account manager");
      }

      // Combine custom features with structured features
      const allFeatures = [...baseFeatures, ...structuredFeatures];

      const enumValue = getEnumValue(plan.name);

      return {
        id: plan.id, // Use unique MongoDB ID for selection
        enumValue: enumValue, // Keep enum value for backend
        name: plan.name,
        price: plan.price?.toString() || "0",
        displayPrice:
          plan.price === 0 || plan.price === "0" ? "Free" : `$${plan.price}`,
        period:
          plan.billingInterval === "monthly"
            ? "month"
            : plan.billingPeriod || "month",
        description:
          plan.description ||
          `Perfect for ${plan.name.toLowerCase()} sales professionals`,
        popular: plan.bestSeller || plan.featured || false,
        features: allFeatures,
        requiresPayment: plan.price > 0,
        stripePriceId: plan.stripePriceId,
        icon: getPackageIcon(plan.name),
        gradient: getPackageGradient(plan.name),
      };
    }) || [];

  // Set default to first available package when plans load
  React.useEffect(() => {
    if (packages && packages.length > 0 && !selectedValue) {
      const freePackage =
        packages.find((pkg) => pkg.enumValue === "free") || packages[0];
      form.setValue("packageType", freePackage.id);
    }
  }, [packages, selectedValue, form]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-slate-50">
      <div className="max-w-4xl mx-auto mt-16 px-4 py-8">
        {/* Progress Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <span className="text-sm font-medium text-gray-600">
              Step 2 of 4
            </span>
            <span className="text-sm font-medium text-gray-600">
              50% Complete
            </span>
          </div>
          <Progress value={50} className="h-2" />
        </div>

        {/* Main Content */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Choose Your Package
          </h1>
          <p className="text-gray-600">
            Select the plan that best fits your sales goals
          </p>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)}>
            <FormField
              control={form.control}
              name="packageType"
              render={({ field }) => (
                <FormItem>
                  <FormControl>
                    <RadioGroup
                      onValueChange={field.onChange}
                      value={field.value}
                      className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-8 mb-8"
                    >
                      {plansLoading ? (
                        <div className="col-span-full flex justify-center py-8">
                          <Loader2 className="h-8 w-8 animate-spin text-purple-600" />
                        </div>
                      ) : (
                        packages.map((pkg) => (
                          <div key={pkg.id} className="relative">
                            <RadioGroupItem
                              value={pkg.id}
                              id={pkg.id}
                              className="peer sr-only"
                            />
                            <label
                              htmlFor={pkg.id}
                              className="flex flex-col cursor-pointer"
                            >
                              <Card
                                className={`peer-checked:ring-4 peer-checked:ring-purple-500 peer-checked:border-purple-500 peer-checked:shadow-2xl peer-checked:scale-105 hover:shadow-xl hover:scale-102 transition-all duration-300 h-full overflow-hidden ${selectedValue === pkg.id ? "ring-4 ring-purple-500 border-purple-500 shadow-2xl scale-105" : ""}`}
                              >
                                {/* Background Gradient */}
                                <div
                                  className={`absolute inset-0 bg-gradient-to-br ${pkg.gradient} opacity-10`}
                                />

                                <CardContent className="relative p-6 flex flex-col h-full">
                                  {pkg.popular && (
                                    <div className="absolute -top-3 left-1/2 transform -translate-x-1/2 z-10">
                                      <Badge className="bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow-lg flex items-center gap-1">
                                        <Star className="h-3 w-3" />
                                        Most Popular
                                      </Badge>
                                    </div>
                                  )}

                                  {/* Package Icon */}
                                  <div className="flex justify-center mb-4">
                                    <div
                                      className={`p-3 rounded-full bg-gradient-to-br ${pkg.gradient} shadow-lg`}
                                    >
                                      {pkg.icon && (
                                        <pkg.icon className="h-8 w-8 text-gray-700" />
                                      )}
                                    </div>
                                  </div>

                                  <div className="text-center mb-6">
