                                    <div className="mb-3">
                                      <span className="text-4xl font-bold bg-gradient-to-r from-purple-600 to-pink-600 bg-clip-text text-transparent">
                                        {pkg.displayPrice}
                                      </span>
                                      {pkg.period !== "forever" && (
                                        <span className="text-gray-600 ml-1">
                                          /{pkg.period}
                                        </span>
