import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Progress } from "@/components/ui/progress";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { ArrowLeft, ArrowRight, Plus, Trash2, Loader2 } from "lucide-react";
import { salesRepInvitesSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";

export default function InviteDecisionMakers() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [decisionMakers, setDecisionMakers] = useState([
    { name: "", email: "" },
  ]);

  // Fetch user's package limits
  const {
    data: packageLimits,
    isLoading: limitsLoading,
    error: limitsError,
  } = useQuery({
    queryKey: ["/api/user-package-limits"],
    retry: 1,
    retryOnMount: false,
    refetchOnWindowFocus: false,
  });

  const form = useForm({
    resolver: zodResolver(salesRepInvitesSchema),
    defaultValues: {
      decisionMakers: [{ name: "", email: "" }],
    },
  });

  const saveInvitesMutation = useMutation({
    mutationFn: async (data) => {
      console.log("Submitting invites data:", data);
      const response = await apiRequest("/api/sales-rep/invites", {
        method: "POST",
        body: JSON.stringify(data),
      });
      return response;
    },
    onSuccess: (data) => {
      console.log("Invites saved successfully:", data);
      toast({
        title: "Registration Complete!",
        description:
          data.message ||
          "Registration completed successfully! Invitations sent.",
      });
      // Redirect to dashboard or thank you page
      setTimeout(() => {
        setLocation("/login");
      }, 2000);
    },
    onError: (error) => {
      console.error("Invites save error:", error);
      console.error("Error details:", {
        message: error.message,
        status: error.status,
      });

      // Handle session expired error specifically
      if (error.message && error.message.includes("Session expired")) {
        toast({
          title: "Session Expired",
          description:
            "Your session has expired. Please refresh the page to continue.",
          variant: "destructive",
        });
        // Show refresh suggestion
        setTimeout(() => {
          if (
            confirm(
              "Your registration is almost complete! Click OK to refresh the page and continue.",
            )
          ) {
            window.location.reload();
          }
        }, 2000);
      } else if (error.message && error.message.includes("Unexpected token")) {
        // Handle JSON parsing errors
        toast({
          title: "Connection Error",
          description:
            "There was a network issue. Please refresh the page and try again.",
          variant: "destructive",
        });
        setTimeout(() => {
          if (
            confirm("Please refresh the page to continue your registration.")
          ) {
            window.location.reload();
          }
        }, 2000);
      } else {
        toast({
          title: "Save Failed",
          description:
            error.message || "Failed to save invitations. Please try again.",
          variant: "destructive",
        });
      }
    },
  });

  const onSubmit = (data) => {
    // Filter out empty entries
    const validDecisionMakers = data.decisionMakers.filter(
      (dm) => dm.name || dm.email,
    );

    // Validate against plan limits
    const maxInvitations = packageLimits?.maxInvitations || 3;
    if (validDecisionMakers.length > maxInvitations) {
      toast({
        title: "Invitation Limit Exceeded",
        description: `Your ${packageLimits?.planName || "Free"} plan allows up to ${maxInvitations} invitations only.`,
        variant: "destructive",
      });
      return;
    }

    saveInvitesMutation.mutate({ decisionMakers: validDecisionMakers });
  };

  const addDecisionMaker = () => {
    // Use dynamic limit from user's package
    const maxInvitations = packageLimits?.maxInvitations || 3;
    if (decisionMakers.length < maxInvitations) {
      const newDecisionMakers = [...decisionMakers, { name: "", email: "" }];
      setDecisionMakers(newDecisionMakers);
      form.setValue("decisionMakers", newDecisionMakers);
    }
  };

  const removeDecisionMaker = (index) => {
    if (decisionMakers.length > 1) {
      const newDecisionMakers = decisionMakers.filter((_, i) => i !== index);
      setDecisionMakers(newDecisionMakers);
      form.setValue("decisionMakers", newDecisionMakers);
    }
  };

  const skipStep = () => {
    // Submit empty invites to complete registration
    saveInvitesMutation.mutate({ decisionMakers: [] });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-slate-50">
      <div className="max-w-2xl mx-auto mt-16 px-4 py-8">
        {/* Progress Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <span className="text-sm font-medium text-gray-600">
              Step 4 of 4
            </span>
            <span className="text-sm font-medium text-gray-600">
              100% Complete
            </span>
          </div>
          <Progress value={100} className="h-2" />
        </div>

        {/* Main Form Card */}
        <Card className="shadow-lg">
          <CardContent className="p-8">
            {limitsLoading ? (
              <div className="text-center mb-8">
                <div className="animate-spin w-6 h-6 border-2 border-primary border-t-transparent rounded-full mx-auto mb-4"></div>
                <p className="text-gray-600">
                  Loading your plan information...
                </p>
              </div>
            ) : limitsError ? (
              <div className="text-center mb-8">
                <h1 className="text-3xl font-bold text-gray-900 mb-2">
                  Invite Decision Makers
                </h1>
                <p className="text-gray-600">
                  Add up to 3 decision makers you'd like to connect with
                  (optional)
                </p>
                <div className="mt-2">
                  <span className="text-sm text-gray-500">
                    {decisionMakers.length} of 3 decision makers added
                  </span>
                  <span className="ml-2 text-xs bg-gray-100 text-gray-600 px-2 py-1 rounded-full">
                    Default Limits
                  </span>
                </div>
              </div>
            ) : (
              <div className="text-center mb-8">
                <h1 className="text-3xl font-bold text-gray-900 mb-2">
                  Invite Decision Makers
                </h1>
                <p className="text-gray-600">
                  Add up to {packageLimits?.maxInvitations || 3} decision makers
                  you'd like to connect with (optional)
                </p>
                <div className="mt-2">
                  <span className="text-sm text-gray-500">
                    {decisionMakers.length} of{" "}
                    {packageLimits?.maxInvitations || 3} decision makers added
                  </span>
                  {packageLimits?.planName && (
                    <span className="ml-2 text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded-full">
                      {packageLimits.planName} Plan
                    </span>
                  )}
                </div>
                {packageLimits?.maxCallCredits && (
                  <div className="mt-1">
                    <span className="text-xs text-gray-400">
                      Monthly Credits: {packageLimits.maxCallCredits}
                    </span>
                  </div>
                )}
              </div>
            )}

            <Form {...form}>
              <form
                onSubmit={form.handleSubmit(onSubmit)}
                className="space-y-6"
              >
                {decisionMakers.map((_, index) => (
                  <div key={index} className="flex gap-4 items-end">
                    <div className="flex-1">
                      <FormField
                        control={form.control}
                        name={`decisionMakers.${index}.name`}
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="text-sm font-medium text-gray-700">
                              Name
                            </FormLabel>
                            <FormControl>
                              <Input
                                placeholder="Decision maker's name"
                                {...field}
                                onChange={(e) => {
                                  field.onChange(e);
                                  const newDecisionMakers = [...decisionMakers];
                                  newDecisionMakers[index].name =
                                    e.target.value;
                                  setDecisionMakers(newDecisionMakers);
                                }}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    <div className="flex-1">
                      <FormField
                        control={form.control}
                        name={`decisionMakers.${index}.email`}
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="text-sm font-medium text-gray-700">
                              Email
                            </FormLabel>
                            <FormControl>
                              <Input
                                type="email"
                                placeholder="email@company.com"
                                {...field}
                                onChange={(e) => {
                                  field.onChange(e);
                                  const newDecisionMakers = [...decisionMakers];
                                  newDecisionMakers[index].email =
                                    e.target.value;
                                  setDecisionMakers(newDecisionMakers);
                                }}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    {decisionMakers.length > 1 && (
                      <Button
                        type="button"
                        variant="outline"
                        size="icon"
                        onClick={() => removeDecisionMaker(index)}
                        className="mb-2"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    )}
                  </div>
                ))}

                <Button
                  type="button"
                  variant="outline"
                  onClick={addDecisionMaker}
                  disabled={
                    decisionMakers.length >=
                    (packageLimits?.maxInvitations || 3)
                  }
                  className="w-full flex items-center gap-2"
                >
                  <Plus className="h-4 w-4" />
                  {decisionMakers.length >= (packageLimits?.maxInvitations || 3)
                    ? `Maximum ${packageLimits?.maxInvitations || 3} Decision Makers Reached`
                    : "Add Another Decision Maker"}
                </Button>

                {/* Navigation Buttons */}
                <div className="flex justify-between pt-6">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() =>
                      setLocation("/signup/sales-rep/professional-info")
                    }
                    className="flex items-center gap-2"
                  >
                    <ArrowLeft className="h-4 w-4" />
                    Back
                  </Button>

                  <div className="flex gap-2">
                    <Button
                      type="button"
                      variant="ghost"
                      onClick={skipStep}
                      className="text-gray-600"
                    >
                      Skip for now
                    </Button>

                    <Button
                      type="submit"
                      disabled={saveInvitesMutation.isPending}
                      className="flex items-center gap-2 bg-blue-600 hover:bg-purple-700"
                    >
                      {saveInvitesMutation.isPending ? (
                        <Loader2 className="h-4 w-4 animate-spin" />
                      ) : (
                        <>
                          Complete Registration
                          <ArrowRight className="h-4 w-4" />
                        </>
                      )}
                    </Button>
                  </div>
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/signup/sales-rep/person.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Progress } from "@/components/ui/progress";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import {
  ArrowLeft,
  Linkedin,
  CheckCircle,
  AlertCircle,
  Eye,
  EyeOff,
  Loader2,
} from "lucide-react";
import { salesRepPersonalInfoSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";

export default function SalesRepPersonalInfo() {
  const [, setLocation] = useLocation();
  const [linkedinVerified, setLinkedinVerified] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const { toast } = useToast();

  const form = useForm({
    resolver: zodResolver(salesRepPersonalInfoSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      email: "",
      companyDomain: "",
      linkedinUrl: "",
      password: "",
      confirmPassword: "",
    },
  });

  const linkedinVerifyMutation = useMutation({
    mutationFn: async (linkedinUrl) => {
      console.log("LinkedIn verification API call starting for:", linkedinUrl);
      try {
        const response = await apiRequest("/api/verify-linkedin", {
          method: "POST",
          body: JSON.stringify({ linkedinUrl }),
        });
        console.log("LinkedIn verification API response:", response);
        return response;
      } catch (error) {
        console.error("LinkedIn verification API error:", error);
        throw error;
      }
    },
    onSuccess: (data) => {
      console.log("LinkedIn verification success handler:", data);
      if (data && data.verified) {
        setLinkedinVerified(true);
        toast({
          title: "LinkedIn Verified",
          description:
            data.message ||
            "Your LinkedIn profile has been successfully verified.",
        });
      } else {
        toast({
          title: "Verification Failed",
          description: data?.message || "Unable to verify LinkedIn profile.",
          variant: "destructive",
        });
      }
    },
    onError: (error) => {
      console.error("LinkedIn verification error handler:", error);
      toast({
        title: "Verification Failed",
        description:
          error.message ||
          "Unable to verify LinkedIn profile. Please check the URL.",
        variant: "destructive",
      });
    },
  });

  const savePersonalInfoMutation = useMutation({
    mutationFn: async (data) => {
      console.log(
        "Submitting form data:",
        data,
        "LinkedIn verified:",
        linkedinVerified,
      );
      const response = await apiRequest("/api/sales-rep/personal-info", {
        method: "POST",
        body: JSON.stringify({ ...data, linkedinVerified }),
      });
      return response;
    },
    onSuccess: (data) => {
      console.log("Form submission successful:", data);
      toast({
        title: "Information Saved",
        description: "Your personal information has been saved successfully.",
      });
      setLocation("/signup/sales-rep/package");
    },
    onError: (error) => {
      console.error("Form submission error:", error);
      toast({
        title: "Save Failed",
        description:
          error.message || "Failed to save information. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleLinkedinVerify = () => {
    const linkedinUrl = form.getValues("linkedinUrl");
    console.log("LinkedIn verification attempt:", { linkedinUrl });

    if (!linkedinUrl) {
      toast({
        title: "LinkedIn URL Required",
        description: "Please enter your LinkedIn profile URL first.",
        variant: "destructive",
      });
      return;
    }

    console.log("Starting LinkedIn verification for:", linkedinUrl);
    linkedinVerifyMutation.mutate(linkedinUrl);
  };

  const onSubmit = (data) => {
    console.log("Form submission attempt:", data);
    console.log("Form errors:", form.formState.errors);
    console.log("LinkedIn verified:", linkedinVerified);
    console.log("Form valid:", form.formState.isValid);

    if (!linkedinVerified) {
      toast({
        title: "LinkedIn Verification Required",
        description: "Please verify your LinkedIn profile before proceeding.",
        variant: "destructive",
      });
      return;
    }
    savePersonalInfoMutation.mutate(data);
  };

  const isFormValid =
    Object.keys(form.formState.errors).length === 0 && linkedinVerified;

  return (
    <div className="min-h-screen bg-gradient-to-br mt-16 from-blue-50 to-slate-50">
      <div className="max-w-2xl mx-auto px-4 py-8">
        {/* Progress Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <span className="text-sm font-medium text-gray-600">
              Step 1 of 4
            </span>
            <span className="text-sm font-medium text-gray-600">
              25% Complete
            </span>
          </div>
          <Progress value={25} className="h-2" />
        </div>

        {/* Main Form Card */}
        <Card className="shadow-lg">
          <CardContent className="p-8">
            <div className="text-center mb-8">
              <h1 className="text-3xl font-bold text-gray-900 mb-2">
                Personal Information
              </h1>
              <p className="text-gray-600">
                Let's start with your basic details and LinkedIn verification
              </p>
            </div>

            <Form {...form}>
              <form
                onSubmit={form.handleSubmit(onSubmit)}
                className="space-y-6"
              >
                {/* Name Fields */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="firstName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-sm font-medium text-gray-700">
                          First Name *
                        </FormLabel>
                        <FormControl>
                          <Input
                            placeholder="Enter your first name"
                            {...field}
                            className="mt-1"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="lastName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-sm font-medium text-gray-700">
                          Last Name *
                        </FormLabel>
                        <FormControl>
                          <Input
                            placeholder="Enter your last name"
                            {...field}
                            className="mt-1"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Email */}
                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">
                        Email Address *
                      </FormLabel>
                      <FormControl>
                        <Input
                          type="email"
                          placeholder="Enter your email address"
                          {...field}
                          className="mt-1"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Company Domain */}
                <FormField
                  control={form.control}
                  name="companyDomain"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-sm font-medium text-gray-700">
                        Company Domain *
                      </FormLabel>
                      <FormControl>
                        <Input
                          placeholder="Enter your company domain (e.g., company.com)"
                          {...field}
                          className="mt-1"
                        />
                      </FormControl>
                      <FormMessage />
                      <p className="text-xs text-gray-500 mt-1">
                        This must match your email domain for verification
                      </p>
                    </FormItem>
                  )}
                />

                {/* LinkedIn Verification */}
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
                  <div className="flex items-start space-x-3 mb-4">
                    <div className="w-8 h-8 bg-blue-600 rounded flex items-center justify-center">
                      <Linkedin className="text-white" size={16} />
                    </div>
                    <div className="flex-1">
                      <h3 className="font-semibold text-gray-900 mb-1">
                        LinkedIn Verification Required
                      </h3>
                      <p className="text-sm text-gray-600">
                        We verify all users through LinkedIn to maintain
                        platform quality
                      </p>
                    </div>
                    {linkedinVerified && (
                      <CheckCircle className="text-green-600" size={20} />
                    )}
                  </div>

                  <FormField
                    control={form.control}
                    name="linkedinUrl"
                    render={({ field }) => (
                      <FormItem className="mb-4">
                        <FormLabel className="text-sm font-medium text-gray-700">
                          LinkedIn Profile URL *
                        </FormLabel>
                        <FormControl>
                          <Input
                            placeholder="https://linkedin.com/in/yourprofile"
                            {...field}
                            className="mt-1"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {linkedinVerified ? (
                    <div className="flex items-center text-green-600 text-sm font-medium">
                      <CheckCircle className="mr-2" size={16} />
                      LinkedIn Verified
                    </div>
                  ) : (
                    <Button
                      type="button"
                      onClick={handleLinkedinVerify}
                      disabled={
                        !form.getValues("linkedinUrl") ||
                        linkedinVerifyMutation.isPending
                      }
                      className="bg-blue-600 hover:bg-blue-700"
                    >
                      {linkedinVerifyMutation.isPending ? (
                        <>
                          <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                          Verifying...
                        </>
                      ) : (
                        <>
                          <Linkedin className="mr-2" size={16} />
                          Verify LinkedIn Profile
                        </>
                      )}
                    </Button>
                  )}
                </div>

                {/* Password Fields */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="password"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-sm font-medium text-gray-700">
                          Password *
                        </FormLabel>
                        <FormControl>
                          <div className="relative">
                            <Input
                              type={showPassword ? "text" : "password"}
                              placeholder="Create a secure password"
                              {...field}
                              className="mt-1 pr-10"
                            />
                            <Button
                              type="button"
                              variant="ghost"
                              size="sm"
                              className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                              onClick={() => setShowPassword(!showPassword)}
                            >
                              {showPassword ? (
                                <EyeOff className="h-4 w-4" />
                              ) : (
                                <Eye className="h-4 w-4" />
                              )}
                            </Button>
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="confirmPassword"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="text-sm font-medium text-gray-700">
                          Confirm Password *
                        </FormLabel>
                        <FormControl>
                          <div className="relative">
                            <Input
                              type={showConfirmPassword ? "text" : "password"}
                              placeholder="Confirm your password"
                              {...field}
                              className="mt-1 pr-10"
                            />
                            <Button
                              type="button"
                              variant="ghost"
                              size="sm"
                              className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                              onClick={() =>
                                setShowConfirmPassword(!showConfirmPassword)
                              }
                            >
                              {showConfirmPassword ? (
                                <EyeOff className="h-4 w-4" />
                              ) : (
                                <Eye className="h-4 w-4" />
                              )}
                            </Button>
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Form Errors */}
                {Object.keys(form.formState.errors).length > 0 && (
                  <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                    <div className="flex items-center space-x-2 text-red-800">
                      <AlertCircle size={16} />
                      <span className="font-medium">
                        Please fix the following errors:
                      </span>
                    </div>
                  </div>
                )}
              </form>
            </Form>
          </CardContent>
        </Card>

        {/* Navigation */}
        <div className="flex justify-between items-center mt-8">
          <Button
            variant="ghost"
            onClick={() => setLocation("/")}
            className="text-gray-600"
          >
            <ArrowLeft className="mr-2" size={16} />
            Back
          </Button>
          <Button
            onClick={form.handleSubmit(onSubmit)}
            disabled={savePersonalInfoMutation.isPending}
            className="bg-purple-600 hover:bg-purple-700 px-8"
          >
            {savePersonalInfoMutation.isPending ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Saving...
              </>
            ) : (
              "Next"
            )}
          </Button>
        </div>
      </div>
    </div>
  );
}

